typedef long long ll;
typedef vector<int> VI;
typedef pair<int,int> PII;

#define REP(i,s,t) for(int i=(s);i<(t);i++)
#define FILL(x,v) memset(x,v,sizeof(x))

const int INF = (int)1E9 + (int)1E6;
#define MAXN 3005

bool v[MAXN];
int dst[MAXN];
vector<PII> adj[MAXN];
map<PII, int> lowc, highc, cnt;
struct dij {
  int x, d;
  dij(int _x, int _d): x(_x), d(_d) {}
  bool operator < (const dij &ano) const {
    return d > ano.d;
  }
};
class Solution {
public:
  int reachableNodes(vector<vector<int>>& edges, int M, int N) {
    FILL(v, 0);
    REP(i,0,N) adj[i].clear();
    lowc.clear(); highc.clear(); cnt.clear();
    REP(i,0,edges.size()) {
      int x = edges[i][0], y = edges[i][1], c = edges[i][2];
      adj[x].push_back(PII(y, c));
      adj[y].push_back(PII(x, c));
      PII e = PII(x, y);
      lowc[e] = highc[e] = 0;
      cnt[e] = c;
    }
    priority_queue<dij> pq;
    REP(i,0,N) dst[i] = INF;
    dst[0] = 0;
    pq.push(dij(0, 0));
    while (pq.size()) {
      dij now = pq.top(); pq.pop();
      if (now.d > dst[now.x]) continue;
      if (now.d > M) continue;
      int x = now.x;
      int sz = adj[x].size();
      REP(i,0,sz) {
        int y = adj[x][i].first, c = adj[x][i].second;
        int nd = now.d + c + 1;
        PII e = x < y ? PII(x, y) : PII(y, x);
        if (nd <= M) {
          lowc[e] = highc[e] = c;
          if (nd < dst[y]) {
            dst[y] = nd;
            pq.push(dij(y, nd));
          }
        } else {
          int s = M - now.d;
          if (x < y) lowc[e] = max(lowc[e], s);
          else highc[e] = max(highc[e], s);
        }
      }
    }
    int ans = 0;
    REP(i,0,N) if (dst[i] < INF) ans++;
    for (auto it = lowc.begin(); it != lowc.end(); it++) {
      int l = it->second, h = highc[it->first];
      ans += min(l + h, cnt[it->first]);
    }
    return ans;
  }
};

	class Solution {
	    public int reachableNodes(int[][] edges, int M, int n) {
	    	int m = edges.length;
	        int[] from = new int[m];
	        int[] to = new int[m];
	        int[] ws = new int[m];
	        for(int i = 0;i < m;i++){
	        	from[i] = edges[i][0];
	        	to[i] = edges[i][1];
	        	ws[i] = edges[i][2]+1;
	        }
	        int[][][] g = packWU(n, from, to, ws);

	        long[] td = dijkl(g, 0);

	        int ret = 0;
	        for(int i = 0;i < m;i++){
	        	long l = td[from[i]];
	        	long r = td[to[i]];
	        	long w = ws[i];
	        	if(l > M && r > M)continue;

	        	// l+k = r+(w-k)
	        	// k = (r-l+w)/2
	        	long ll = (r-l+w)/2;
	        	// [0,ll], [ll+1,w]
	        	if(l > M){
	        	}else if(l+ll <= M){
	        		ret += ll+1;
	        	}else{
	        		ret += M-l+1;
	        	}
	        	long rr = (w-(ll+1));
	        	if(r > M){
	        	}else if(r+rr <= M){
	        		ret += rr+1;
	        	}else{
	        		ret += M-r+1;
	        	}

	        	if(l <= M)ret--;
	        	if(r <= M)ret--;
//	        	tr(from[i], to[i], l, r, w, ret);
	        	// 012345678765
	        }

	        for(int i = 0;i < n;i++){
	        	if(td[i] <= M)ret++;
	        }

	        return ret;
	    }

	    int[][][] packWU(int n, int[] from, int[] to, int[] w) {
			int[][][] g = new int[n][][];
			int[] p = new int[n];
			for (int f : from)
				p[f]++;
			for (int t : to)
				p[t]++;
			for (int i = 0; i < n; i++)
				g[i] = new int[p[i]][2];
			for (int i = 0; i < from.length; i++) {
				--p[from[i]];
				g[from[i]][p[from[i]]][0] = to[i];
				g[from[i]][p[from[i]]][1] = w[i];
				--p[to[i]];
				g[to[i]][p[to[i]]][0] = from[i];
				g[to[i]][p[to[i]]][1] = w[i];
			}
			return g;
		}

		long[] dijkl(int[][][] g, int from)
		{
			int n = g.length;
			long[] td = new long[n];

			Arrays.fill(td, Long.MAX_VALUE / 2);
			MinHeapL q = new MinHeapL(n);
			q.add(from, 0);
			td[from] = 0;

			while(q.size() > 0){
				int cur = q.argmin();
				q.remove(cur);

				for(int[] e : g[cur]){
					int next = e[0];
					long nd = td[cur] + e[1];
					if(nd < td[next]){
						td[next] = nd;
						q.update(next, nd);
					}
				}
			}

			return td;
		}

		class MinHeapL {
			public long[] a;
			public int[] map;
			public int[] imap;
			public int n;
			public int pos;
			public long INF = Long.MAX_VALUE;

			public MinHeapL(int m)
			{
				n = Integer.highestOneBit((m+1)<<1);
				a = new long[n];
				map = new int[n];
				imap = new int[n];
				Arrays.fill(a, INF);
				Arrays.fill(map, -1);
				Arrays.fill(imap, -1);
				pos = 1;
			}

			public long add(int ind, long x)
			{
				int ret = imap[ind];
				if(imap[ind] < 0){
					a[pos] = x; map[pos] = ind; imap[ind] = pos;
					pos++;
					up(pos-1);
				}
				return ret != -1 ? a[ret] : x;
			}

			public long update(int ind, long x)
			{
				int ret = imap[ind];
				if(imap[ind] < 0){
					a[pos] = x; map[pos] = ind; imap[ind] = pos;
					pos++;
					up(pos-1);
				}else{
					a[ret] = x;
					up(ret);
					down(ret);
				}
				return x;
			}

			public long remove(int ind)
			{
				if(pos == 1)return INF;
				if(imap[ind] == -1)return INF;

				pos--;
				int rem = imap[ind];
				long ret = a[rem];
				map[rem] = map[pos];
				imap[map[pos]] = rem;
				imap[ind] = -1;
				a[rem] = a[pos];
				a[pos] = INF;
				map[pos] = -1;

				up(rem);
				down(rem);
				return ret;
			}

			public long min() { return a[1]; }
			public int argmin() { return map[1]; }
			public int size() {	return pos-1; }

			private void up(int cur)
			{
				for(int c = cur, p = c>>>1;p >= 1 && a[p] > a[c];c>>>=1, p>>>=1){
					long d = a[p]; a[p] = a[c]; a[c] = d;
					int e = imap[map[p]]; imap[map[p]] = imap[map[c]]; imap[map[c]] = e;
					e = map[p]; map[p] = map[c]; map[c] = e;
				}
			}

			private void down(int cur)
			{
				for(int c = cur;2*c < pos;){
					int b = a[2*c] < a[2*c+1] ? 2*c : 2*c+1;
					if(a[b] < a[c]){
						long d = a[c]; a[c] = a[b]; a[b] = d;
						int e = imap[map[c]]; imap[map[c]] = imap[map[b]]; imap[map[b]] = e;
						e = map[c]; map[c] = map[b]; map[b] = e;
						c = b;
					}else{
						break;
					}
				}
			}
		}

	}

class Solution(object):
    def reachableNodes(self, edges, M, N):
        """
        :type edges: List[List[int]]
        :type M: int
        :type N: int
        :rtype: int
        """
        b=[(0,M)]
        seen={0:M}

        m=collections.defaultdict(dict)
        rec={}
        for s,e,c in edges:
            m[s][e]=c
            m[e][s]=c
            rec[(s,e)]=0
            rec[(e,s)]=0

        i=0
        while i<len(b):
            cur,left=b[i]
            for t in m[cur]:
                step=left-m[cur][t]-1
                if step>=0 and (t not in seen or seen[t]<step):
                    seen[t]=step
                    b.append((t,step))
                    rec[(cur,t)]=m[cur][t]
                else:
                    rec[(cur,t)]=max(rec[(cur,t)],left)
            i+=1
        ans=len(seen)
        for s,e,c in edges:
            ans+=min(c,rec[(s,e)]+rec[(e,s)])
        return ans

/**
 * @param {number[][]} edges
 * @param {number} M
 * @param {number} N
 * @return {number}
 */
var reachableNodes = function(edges, M, N) {
    const MAXINT = 90071992547409
    const elist = {}
    for (let i = 0; i < edges.length; i++) {
        const [s, e, v] = edges[i]
        if (!elist[s]) {
            elist[s] = []
        }
        if (!elist[e]) {
            elist[e] = []
        }
        elist[s].push([e, v + 1, i])
        elist[e].push([s, v + 1, i])
    }
    if (!elist[0]) return 1
    let S = new Array(N).fill(false)
    let dist = new Array(N).fill(MAXINT)
    let prev = new Array(N).fill(0)
    for (let [e, v] of elist[0]) {
        dist[e] = v
    }
    for (let i = 1; i < N; i++) {
        if(dist[i] === MAXINT) {  
            prev[i] = -1
    } else { 
            prev[i] = 0
        }
    }
    dist[0] = 0;
    S[0] = true; 
    for(let i = 2; i < N; i++) {
        let mindist = MAXINT;
        let u = 0
        for(let j = 1; j < N; ++j) {
            if((!S[j]) && dist[j] < mindist) {
                u = j
                mindist = dist[j]
            }
        }
        S[u] = true
        for (const [j, v] of elist[u]) {
            if(!S[j]) {
                if(dist[u] + v < dist[j]) {
                    dist[j] = dist[u] + v
                    prev[j] = u
                }
            }
        }
    }
    let ledges = edges.map(i => ({ [i[0]]: 0, [i[1]]: 0 }))
    let ans = 0
    for (let i = 0; i < N; i++) {
        let cd = M - dist[i]
        if (cd < 0) continue
        ans++
        for (const [e, v, id] of elist[i]) {
            ledges[id][i] = Math.min(cd, v)
        }
    }
    for (let i = 0; i < ledges.length; i++) {
        let [a, b] = Object.values(ledges[i])
        ans += Math.min(a + b, edges[i][2])
    }
    return ans
};

