class Solution {

	public long repairCars(int[] ranks, int cars) {
		long left = 1, right = 100000000000000L;
		while (left < right) {
			long mid = (left + right) / 2, count = 0;
			for (int rank : ranks) {
				count += Math.sqrt(mid / rank);
			}
			if (count < cars) {
				left = mid + 1;
			} else {
				right = mid;
			}
		}
		return left;
	}
