class Solution(object):
    def repairCars(self, ranks, cars):
        """
        :type ranks: List[int]
        :type cars: int
        :rtype: int
        """
        l = 1
        r = min(ranks)*cars*cars
        while l < r:
            time = (l+r) // 2
            repair = 0
            for rank in ranks:
                repair += int((time/rank)**(0.5))
            if repair >= cars:
                r = time
            else:
                l = time+1
        return r

