var repairCars = function(ranks, cars) {
    let l = 0, r = 1e14, ans = r;
    for (; l <= r; )
    {
        let mid = Math.floor((l + r) / 2);
        let cur = 0;
        for (let x of ranks)
        {
            let y = Math.floor(Math.sqrt((mid / x)));
            cur += y;
        }
        if (cur >= cars)
        {
            ans = mid;
            r = mid - 1;
        }
        else
            l = mid + 1;
    }
    return ans;
};

