        let mut high: i64 = 100_000_000_000_000;

        fn fix_cnt(r: i64, max_time: i64, max_cars: i64) -> i64 {
            let mut low = 0;
            let mut high = max_cars + 1;
            while low + 1 < high {
                let mid = (low + high) / 2;
                if r * mid * mid <= max_time {
                    low = mid;
                }
                else {
                    high = mid;
                }
            }
            low
        }

        while low + 1 < high {
            let mid = low + (high - low) / 2;

            let fixable: i64 = ranks.iter().map(|&r| fix_cnt(r as i64, mid, cars)).sum();

            if fixable >= cars {
                high = mid;
            }
            else {
                low = mid;
            }
        }
        high
    }
}

