class Solution:
    def countReversableForLength(self, length: int) -> int:
        return 4 * 5**(length - 1);

    def foobar(self, i: int, c: str) -> int:
        res = 1 if i > 0 else 0
        if c == '7':
            return res + 2
        return res + 1

    def skidiki(self, i: int, c: str) -> int:
        if c == '0':
            return 0
        res = 1 if i > 0 else 0
        if c == '1':
            return res
        if c == '6':
            return res + 1
        if c == '8':
            return res + 2
        if c == '9':
            return res + 3

    def check(self, s: int) -> bool:
        for c in str(s):
            if c in '23457':
                return False
        return True

    def countReversable(self, N: int) -> int:
        s = str(N)
        ans = 0
        for i in range(1, len(s)):
            ans += self.countReversableForLength(i)
        for i, c in enumerate(s):
            if c in '23457':
                ans += self.foobar(i, c) * 5**(len(s) - i - 1)
                return ans
            else:
                ans += self.skidiki(i, c) * 5**(len(s) - i - 1)
        return ans + 1

    def inverse(self, c: str) -> str:
        if c == '0':
            return '0'
        if c == '1':
            return '1'
        if c == '6':
            return '9'
        if c == '8':
            return '8'
        if c == '9':
            return '6'

    def reverse(self, x: int) -> str:
        s = str(x)
        for c in s:
            if c not in "01689":
                return None
        s = s[::-1]
        return ''.join([self.inverse(c) for c in s])

    def confusingNumberII(self, N: int) -> int:
        ans = self.countReversable(N)

        if N >= 1:
            ans -= 1

        if N >= 8:
            ans -= 1

        i = 1
        while True:
            s = self.reverse(i)
            if not s:
                i += 1
                continue
            x = int(str(i) + s)
            if x <= N:
                ans -= 1
            else:
                break
            i += 1

        i = 1
        while True:
            s = self.reverse(i)
            if not s:
                i += 1
                continue
            x = int(str(i) + '0' + self.reverse(i))
            y = int(str(i) + '1' + self.reverse(i))
            z = int(str(i) + '8' + self.reverse(i))
            if x <= N:
                ans -= 1
            if y <= N:
                ans -= 1
            if z <= N:
                ans -= 1
            if x > N and y > N and x > N:
                break
            i += 1

        return ans

