var isValid = function(num) {
    var map = {};
    map[0] = 0;
    map[1] = 1;
    map[6] = 9;
    map[8] = 8;
    map[9] = 6;

    var newNum = 0;
    var myNum = num;
    while(myNum > 0) {
        newNum = newNum * 10 + map[myNum % 10];
        myNum = Math.floor(myNum / 10);
    }

    return num !== newNum;
}

long rotate(int num, int *conf) {
    long res = 0;
    while (num > 0) {
        int n = num % 10;
        res = res * 10 + conf[n];
        num /= 10;
    }
    return res;
}

int dig(int num) {
    int res = 0;
    while (num > 0) {
        res++;
        num /= 10;
    }
    return res;
}

void dfs(int d, int total_dig, int *valid, int vsize, int *conf, long val, long max_val, int *res, bool *selected) {
    if (d == total_dig) {
        if (val <= max_val) {
            long new_val = rotate(val, conf);
            if (new_val != val && !selected[val]) {
                // printf("val %d, new_val %d\n", val, new_val);
                selected[val] = true;
                ++*res;
            }
        }
        return;
    }
    int i;
    for (i = 0; i < vsize; i++) {
        dfs(d+1, total_dig, valid, vsize, conf, (long) (val * 10 + valid[i]), max_val, res, selected);
    }
}

