        }
        int n=500,i,j;
        for(i=1;i<=n;i++)a[i].clear();
        for(auto e:pairs)if(d[e[0]]<d[e[1]])a[e[0]].push_back(e[1]);
        else a[e[1]].push_back(e[0]);
        for(i=1;i<=n;i++)if(b[i])
        {
            j=0;
            for(auto c:a[i])if(!j||d[c]<d[j]||d[c]==d[j]&&c>j)j=c;
            f[i]=j;
        }
        for(i=1,j=0;i<=n;i++)if(b[i]&&!f[i])j++;
        if(!j||j>1)return 0;
        for(i=1;i<=n;i++)if(b[i])for(j=f[i];j;j=f[j])if(!g[i][j])return 0;
        memset(g,0,sizeof(g));
        for(i=1;i<=n;i++)if(b[i])for(j=f[i];j;j=f[j])g[i][j]=g[j][i]=1;
        for(auto e:pairs)if(!g[e[0]][e[1]])return 0;
        for(i=1;i<=n;i++)if(b[i]&&f[i]&&d[i]==d[f[i]])return 2;
        return 1;
    }
};

class Solution {
	public int checkWays(int[][] pairs) {
		int m = pairs.length;
		int n = 501;
		int[] deg = new int[n];
		for(int[] p : pairs){
			deg[p[0]]++;
			deg[p[1]]++;
		}
		boolean[][] g = new boolean[n][n];
		for(int[] p : pairs){
			g[p[0]][p[1]] = g[p[1]][p[0]] = true;
		}

		int[][] di = new int[n][];
		for(int i = 0;i < n;i++){
			di[i] = new int[]{deg[i], i};
		}
		Arrays.sort(di, (x, y) -> {
			if (x[0] != y[0]) return -(x[0] - y[0]);
			return x[1] - y[1];
		});

		int[] par = new int[n];
		Arrays.fill(par, -1);
		for(int i = 1;i < n;i++){
			for(int j = i-1;j >= 0;j--){
				if(g[di[j][1]][di[i][1]]){
					par[di[i][1]] = di[j][1];
					break;
				}
			}
		}

		boolean[][] h = new boolean[n][n];
		for(int i = 0;i < n;i++){
			for(int j = par[i];j != -1;j = par[j]){
				h[i][j] = h[j][i] = true;
			}
		}

		for(int i = 0;i < n;i++){
			if(!Arrays.equals(g[i], h[i])){
				return 0;
			}
		}

		Arrays.sort(di, (x, y) -> {
			if (x[0] != y[0]) return -(x[0] - y[0]);
			return -(x[1] - y[1]);
		});

		int[] par2 = new int[n];
		Arrays.fill(par2, -1);
		for(int i = 1;i < n;i++){
			for(int j = i-1;j >= 0;j--){
				if(g[di[j][1]][di[i][1]]){
					par2[di[i][1]] = di[j][1];
					break;
				}
			}
		}
		return Arrays.equals(par, par2) ? 1 : 2;
	}
}

class Solution:
    def checkWays(self, pairs: List[List[int]]) -> int:
        n = 0
        a = set()
        for x in pairs:
            n = max(n, x[0], x[1])
            a.add(x[0])
            a.add(x[1])
        d = [0] * (n + 1)
        e = [[] for _ in range(n + 1)]
        for x in pairs:
            d[x[0]] += 1
            d[x[1]] += 1
            e[x[0]].append(x[1])
            e[x[1]].append(x[0])

        roots = set()
        a = list(a)

        def work(a):
            if len(a) == 1:
                return 1
            c = 0
            o = -1
            for x in a:
                if d[x] == len(a) - 1 + len(roots):
                    c += 1
                    o = x
            if c == 0:
                return 0
            roots.add(o)
            f = set()
            aa = []
            for x in a:
                if x not in roots and x not in f:
                    q = [x]
                    f.add(x)
                    t = 0
                    while t < len(q):
                        y = q[t]
                        t += 1
                        for z in e[y]:
                            if z not in roots and z not in f:
                                q.append(z)
                                f.add(z)
                    aa.append(q)
            for b in aa:
                r = work(b)
                if r == 0:
                    return 0
                c = max(r, c)
            roots.remove(o)
            return c

        r = work(a)
        if r > 1:
            return 2
        return r

