var matrixSumQueries = function(n, queries) {
    let row = Array.from({length: n}).map(_ => [0, -1]);
    let col = Array.from({length: n}).map(_ => [0, -1]);
    for (let i = 0; i < queries.length; i++) {
        let [type, index, val] = queries[i];
        if (type === 0) {
            row[index][0] = val;
            row[index][1] = i;
        } else {
            col[index][0] = val;
            col[index][1] = i;
        }

    }
    col = col.sort((a, b) => a[1] - b[1]);
    row = row.sort((a, b) => a[1] - b[1])
    let res = 0;
    let tempSum = 0;
    let tempIndex = n - 1;
    for (let i = n - 1; i >= 0; i--) {
        let rowVal = row[i][0];
        let rowIndex = row[i][1];
        while (tempIndex >= 0 && col[tempIndex][1] > rowIndex) {
            tempSum += col[tempIndex][0];
            tempIndex -= 1;
        }
        res += (tempIndex + 1) * rowVal + tempSum
    }
    // console.log(row, col)
    return res;
};

class Solution(object):
    def maxEvents(self, events):
        events = [[s, e+1] for s, e in events]
        M = max(e for s,e in events)
        A = [[] for _ in xrange(M+1)]
        for s, e in events:
            A[s].append(e)

        pq = []
