class Solution {
public:
    int maxEvents(vector<vector<int>>& events) {
        int n = events.size();
        sort(events.begin(), events.end(), [](auto& u, auto& v) {
            return u[0] < v[0];
        });
        int pos = 0, ans = 0;
        priority_queue<int, vector<int>, greater<int>> q;
        for (int i = 1; i <= 100000; ++i) {
            while (pos < n && events[pos][0] == i) {
                q.push(events[pos][1]);
                ++pos;
            }
            while (!q.empty() && q.top() < i) {
                q.pop();
            }
            if (!q.empty()) {
                q.pop();
                ++ans;
            }
        }
