class Solution:
    def maxEvents(self, events: List[List[int]]) -> int:
        heapq.heapify(events)
        d = events[0][0]
        ret = 0
        n = len(events)
        while events:
            s, e = heapq.heappop(events)
            if s < d:
                if e >= d:
                    heapq.heappush(events, [d, e])
            else:
                ret += 1
                d = max(s, d) + 1
        return ret

