var maxEvents = function(events) {
    const se = events.sort((a,b) => a[0] !== b[0] ? a[0] - b[0] : a[1] - b[1]);
    let r = 0;
    let c = se[0][0];
    let w = [];
    let sum = 0;
    while (r < se.length || w.length > 0) {
        while (w.length > 0 && w[0] < c) {
            w.splice(0, 1);
        }
        while (r < se.length && se[r][0] <= c) {
            let ll = 0;
            let rr = w.length - 1;
            while (ll <= rr) {
                let m = Math.round((ll+rr) / 2);
                if (w[m] < se[r][1]) {
                    ll = m + 1;
                } else {
                    rr = m - 1;
                }
            }
            w.splice(rr + 1, 0, se[r][1]);
            r++;
        }
        if (w.length > 0) {
            w.splice(0, 1);
            sum++;
        }
        c++;
    }
    return sum;
};

