var minimumReplacement = function(nums) {
    let N = nums.length, next = nums[N - 1], ans = 0
    for (let i = N - 2; i >= 0; i--) {
        let num = nums[i]
        let slice = Math.ceil((num - next) / next)
        ans += slice
        next = num % next === 0 ? next : Math.floor(num / (slice + 1))
    }
    return ans
};

class Solution:
    def colorTheGrid(self, R, C):
        MOD = 10 ** 9 + 7
        def nodup(row):
            for i in range(len(row) - 1):
                if row[i] == row[i+1]: return False
            return True

        states = [c for c in itertools.product(range(3), repeat=R) if nodup(c)]
        adj = [[] for _ in states]
        for i1, s1 in enumerate(states):
            for i2, s2 in enumerate(states):
                if all(s1[i] != s2[i] for i in range(R)):
                    adj[i1].append(i2)

        dp = [1] * len(states)
        for _ in range(C-1):
            ndp = [0] * len(states)
            for i1, row in enumerate(adj):
                for i2 in row:
                    ndp[i2] += dp[i1]
                    ndp[i2] %= MOD
            dp = ndp

        return sum(dp) % MOD

