class Solution {
public:
    int colorTheGrid(int m, int n) {
        vector<int> p(m + 1);
        for (int i = 0; i <= m; i += 1) p[i] = i ? p[i - 1] * 3 : 1;
        vector<int> dp(p[m], 1);
        for (int i = 0; i < p[m]; i += 1) {
            vector<int> c(m);
            for (int j = 0; j < m; j += 1)
                c[j] = i / p[j] % 3;
            for (int j = 1; j < m; j += 1)
                if(c[j] == c[j - 1])
                    dp[i] = 0;
        }
        for (int i = m; i < m * n; i += 1) {
            vector<int> tmp(p[m]);
            for (int j = 0; j < p[m]; j += 1)
                for (int k = 0; k < 3; k += 1) {
                    if (i % m != 0 and k == j % 3) continue;
                    if (k == j / p[m - 1]) continue;
                    add(tmp[j * 3 % p[m] + k], dp[j]);
                }
            swap(dp, tmp);
        }
        int ans = 0;
        for (int x : dp) add(ans, x);
        return ans;
    }
};

const MOD: usize = 1_000_000_007;

impl Solution {
    pub fn color_the_grid(m: i32, n: i32) -> i32 {
        let m = m as usize;
        let n = n as usize;

        let mut upper = 1;
        for i in 0..m {
            upper *= 3;
        }

        let mut codes = vec![];
        for i in 0..upper {
            let mut code = vec![0; m];
            let mut num = i;
            for j in 0..m {
                if num == 0 {
                    break;
                }
                code[j] = num % 3;
                num /= 3;
            }
            let mut valid = true;
            for j in 0..m - 1 {
                if code[j] == code[j + 1] {
                    valid = false;
                    break;
                }
            }
            if valid {
                codes.push(code);
            }
        }

        let k = codes.len();
        let mut trans = vec![vec![]; k];
        for i in 0..k {
            for j in i + 1..k {
                let mut valid = true;
                for t in 0..m {
                    if codes[i][t] == codes[j][t] {
                        valid = false;
                        break;
                    }
                }
                if valid {
                    trans[i].push(j);
                    trans[j].push(i);
                }
            }
        }

        let mut dp = vec![1; k];
        for i in 1..n {
            let mut ndp = vec![0; k];
            for j in 0..k {
                for &t in trans[j].iter() {
                    ndp[t] = (ndp[t] + dp[j]) % MOD;
                }
            }
            dp = ndp;
        }

        let mut ans = 0;
        for i in 0..k {
            ans = (ans + dp[i]) % MOD;
        }

        ans as i32
    }
}

