/**
 * @param {number} m
 * @param {number} n
 * @return {number}
 */
var colorTheGrid = function(m, n) {
    let cands = valid3(m)
    let colors = new Map()
    for (let cand of cands) colors.set(cand, 1)
    // console.log(colors)

    let nexts = new Map()
    for (let u of cands) {
        let set = new Set()
        for (let v of cands) {
            if (cross(u, v)) continue
            set.add(v)
        }
        nexts.set(u, set)
    }

    for (let i = 1; i < n; i++) {
        let ncolors = new Map()
        for (let u of cands) {
            let count = 0
            for (let v of nexts.get(u)) {
                count += colors.get(v)
                count %= MOD
            }
            ncolors.set(u, count)
        }
        colors = ncolors
    }
    let ans = 0
    for (let v of colors.values()) {
        ans += v
        ans %= MOD
    }
    return ans

    function cross(u, v) {
        for(let i = 0; i < m; i++) {
            if ((u % 3) === (v % 3)) return true
            u = Math.floor(u / 3)
            v = Math.floor(v / 3)
        }
        return false
    }
};
function valid3(m) {
    return [...dfs(-1, m)]

    function *dfs(prev, m) {
        if (m === 0) return yield 0
        for(let i = 0; i < 3; i++) {
            if (prev === i) continue

            let cand = i * (3 ** (m - 1))
            for (let rem of dfs(i, m - 1)) {
                yield cand + rem
            }
        }
    }
}

from collections import defaultdict


# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def amountOfTime(self, root: Optional[TreeNode], start: int) -> int:
        if not root:
            return 0

        tree = defaultdict(list)
        stack = [root]
        while stack:
            node = stack.pop()
            if node.left is not None:
                tree[node.val].append(node.left.val)
                tree[node.left.val].append(node.val)
                stack.append(node.left)
            if node.right is not None:
                tree[node.val].append(node.right.val)
                tree[node.right.val].append(node.val)
                stack.append(node.right)

        visited = {start}
        queue = [start]
        depth = [0]
        for node, d in zip(queue, depth):
            for child in tree[node]:
                if child not in visited:
                    visited.add(child)
                    queue.append(child)
                    depth.append(d + 1)

        return depth[-1]

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
    int mx = 0;
    vector<vector<int>> e;

    void dfs1(TreeNode *node) {
        if (node == nullptr) return;
        mx = max(mx, node->val);
        dfs1(node->left); dfs1(node->right);
    }

    void dfs2(TreeNode *node) {
        if (node->left != nullptr) {
            e[node->val].push_back(node->left->val);
            e[node->left->val].push_back(node->val);
            dfs2(node->left);
        }
        if (node->right != nullptr) {
            e[node->val].push_back(node->right->val);
            e[node->right->val].push_back(node->val);
            dfs2(node->right);
        }
    }

    int dfs3(int sn, int fa) {
        int ret = 0;
        for (int fn : e[sn]) if (fn != fa) ret = max(ret, dfs3(fn, sn) + 1);
        return ret;
    }

public:
    int amountOfTime(TreeNode* root, int start) {
        dfs1(root);
        e.resize(mx + 1);
        dfs2(root);
        return dfs3(start, -1);
    }
};

class Solution {
	int[] from = new int[100001];
	int[] to = new int[100001];
	int p = 0;
	public int amountOfTime(TreeNode root, int start) {
		dfs(root, -1);
		int[][] g = packU(100001, from, to, p);
		int[] d = bfs(g, start);
		int max = 0;
		for(int i = 0;i < 100001;i++){
			if(d[i] < Integer.MAX_VALUE / 3){
				max = Math.max(max, d[i]);
			}
		}
		return max;
	}

	public int[] bfs(int[][] g, int s)
	{
		int n = g.length;
		int[] q = new int[n];
		int[] ds = new int[n];
		Arrays.fill(ds, Integer.MAX_VALUE / 2);
		int qp = 0;
		q[qp++] = s;
		ds[s] = 0;
		for(int z = 0;z < qp;z++){
			int cur = q[z];
			for(int e : g[cur]){
				if(ds[e] > ds[cur] + 1){
					ds[e] = ds[cur] + 1;
					q[qp++] = e;
				}
			}
		}
		return ds;
	}


	void dfs(TreeNode cur, int pre)
	{
		if(cur == null)return;
		if(pre != -1) {
			from[p] = pre;
			to[p] = cur.val;
			p++;
		}
		dfs(cur.left, cur.val);
		dfs(cur.right, cur.val);
	}

	public  int[][] packU(int n, int[] from, int[] to) {
		return packU(n, from, to, from.length);
	}

	public  int[][] packU(int n, int[] from, int[] to, int sup) {
		int[][] g = new int[n][];
		int[] p = new int[n];
		for (int i = 0; i < sup; i++) p[from[i]]++;
		for (int i = 0; i < sup; i++) p[to[i]]++;
		for (int i = 0; i < n; i++) g[i] = new int[p[i]];
		for (int i = 0; i < sup; i++) {
			g[from[i]][--p[from[i]]] = to[i];
			g[to[i]][--p[to[i]]] = from[i];
		}
		return g;
	}

}

/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @param {number} start
 * @return {number}
 */
var amountOfTime = function(root, start) {
    let total = 0;
    const a = new Array(100001).fill(0);
    const e = new Array(100001).fill(0).map(item => []);
    const check = (x) => {
        total++;
        a[x.val] = -1;
        if (x.left) {
            e[x.val].push(x.left.val);
            e[x.left.val].push(x.val);
            check(x.left);
        }
        if (x.right) {
            e[x.val].push(x.right.val);
            e[x.right.val].push(x.val);
            check(x.right);
        }
    }
    check(root);
    a[start] = 0;
    let l = 0;
    const r = [ start ];
    while (r.length < total) {
        const cur = r[l];
        for (const temp of e[cur]) {
            if (a[temp] > -1) continue;
            a[temp] = a[cur] + 1;
            r.push(temp);
        }
        l++;
    }
    return a[r[total - 1]];
};

class Solution {
public:
    int minCapability(vector<int>& nums, int k) {
        int lo = 0, hi = 1e9; 
        while (lo < hi) {
            int mid = lo + (hi - lo)/2; 
            int count = 0, val = INT_MIN; 
            for (int i = 0; i < nums.size(); ++i) {
                if (nums[i] <= mid && val + 1 < i) {
                    ++count; 
                    val = i; 
                }
            }
            if (count < k) lo = mid + 1; 
            else hi = mid; 
        }
        return lo; 
    }
};

class Solution {
	public int minCapability(int[] nums, int k) {
		int low = 0, high = 1000000001;
		while(high - low > 1){
			int h = high+low>>1;
			int num = 0;
			boolean done = false;
			for(int v : nums){
				if(v <= h && !done){
					num++;
					done = true;
				}else if(done){
					done = false;
				}
			}
			if(num >= k) {
				high = h;
			}else{
				low = h;
			}
		}
		return high;
	}
}

