const int INF = (int)1e9;

typedef pair<int, int> P;
struct edge{int to, cap, cost, rev;};

struct MincostFlow {
    vector<vector<edge>> G;
    vi h, dist, prevv, preve;

    MincostFlow(int n = 0): G(n), h(n), dist(n), prevv(n), preve(n) {}

    void add_edge(int from, int to, int cap, int cost){
        G[from].push_back((edge){to, cap, cost, (int)G[to].size()});
        G[to].push_back((edge){from, 0, -cost, (int)G[from].size() - 1});
    }

    int min_cost_flow(int s, int t, int f){
        int res = 0;
        // substitute with Bellman-ford/DAG DP for negative edges
        fill(all(h), 0);
        while(f > 0) {
            priority_queue<P, vector<P>, greater<P> > que;
            fill(all(dist), INF);
            dist[s] = 0;
            que.push(P(0, s));
            while(!que.empty()){
                P p = que.top(); que.pop();
                int v = p.second;
                if(dist[v] < p.first) continue;
                for(int i = 0; i < G[v].size(); i++){
                    edge &e = G[v][i];
                    if(e.cap > 0 && dist[e.to] > dist[v] + e.cost + h[v] - h[e.to]){
                        dist[e.to] = dist[v] + e.cost + h[v] - h[e.to];
                        prevv[e.to] = v;
                        preve[e.to] = i;
                        que.push(P(dist[e.to], e.to));
                    }
                }
            }
            if(dist[t] == INF) {
                return -1;
            }
            for(int v = 0; v < sz(h); v++) h[v] += dist[v];

            int d = f;
            for(int v = t; v != s; v = prevv[v]){
                d = min(d, G[prevv[v]][preve[v]].cap);
            }
            f -= d;
            res += d * h[t];
            for(int v = t; v != s; v = prevv[v]){
                edge &e = G[prevv[v]][preve[v]];
                e.cap -= d;
                G[v][e.rev].cap += d;
            }
        }
        return res;
    }
};

class Solution {
public:
    int maximumANDSum(vector<int>& nums, int numSlots) {
        int n = sz(nums);
        int m = numSlots;
        MincostFlow mcmf(n + m + 2);
        int s = n + m, t = s + 1;
        rep(i, 0, n) {
            mcmf.add_edge(s, i, 1, 0);
            rep(j, 0, m) {
                mcmf.add_edge(i, j + n, 1, -(nums[i] & (j + 1)));
            }
        }
        rep(i, 0, m) {
            mcmf.add_edge(i + n, t, 2, 0);
        }
        return -mcmf.min_cost_flow(s, t, n);
    }
};

