def hungarian(orig_cost):
    n = len(orig_cost)
    assert all(len(row) == n for row in orig_cost)
    cost = map(list, orig_cost)
    for x in xrange(n):
        min_cost = min(cost[x])
        for y in xrange(n):
            cost[x][y] -= min_cost
    for y in xrange(n):
        min_cost = min(cost[x][y] for x in xrange(n))
        for x in xrange(n):
            cost[x][y] -= min_cost

    match_x = [-1]*n
    match_y = [-1]*n
    label_x = [0]*n
    label_y = [0]*n
    minx_slack_y = [0]*n
    min_slack_y = [0]*n
    seen_x = [False]*n
    prev_y = [0]*n

    def _augment(x):
        for i in xrange(n):
            seen_x[i] = False
            prev_y[i] = -1
        seen_x[x] = True
        for y in xrange(n):
            min_slack_y[y] = cost[x][y] - label_x[x] - label_y[y]
            minx_slack_y[y] = x
        while True:
            min_slack = float('inf')
            min_x = -1
            min_y = -1
            for y in xrange(n):
                if prev_y[y] == -1 and min_slack_y[y] < min_slack:
                    min_slack = min_slack_y[y]
                    min_x = minx_slack_y[y]
                    min_y = y
            if min_slack > 0:
                for x in xrange(n):
                    if seen_x[x]:
                        label_x[x] += min_slack
                for y in xrange(n):
                    if prev_y[y] == -1:
                        min_slack_y[y] -= min_slack
                    else:
                        label_y[y] -= min_slack
            prev_y[min_y] = min_x
            if match_y[min_y] == -1:
                y = min_y
                while True:
                    x = prev_y[y]
                    nxt = match_x[x]
                    match_x[x] = y
                    match_y[y] = x
                    y = nxt
                    if y == -1:
                        break
                return
            x = match_y[min_y]
            seen_x[x] = True
            for y in xrange(n):
                if prev_y[y] == -1:
                    slack = cost[x][y] - label_x[x] - label_y[y]
                    if min_slack_y[y] > slack:
                        min_slack_y[y] = slack
                        minx_slack_y[y] = x

    # greedy initial match
    for x in xrange(n):
        for y in xrange(n):
            if match_x[x] == -1 and match_y[y] == -1 and cost[x][y] == label_x[x] + label_y[y]:
                match_x[x] = y
                match_y[y] = x

    # augment loop
    while True:
        for x in xrange(n):
            if match_x[x] == -1:
                _augment(x)
                break
        else:
            break

    # compute best cost
    best_cost = sum(orig_cost[x][match_x[x]] for x in xrange(n))
    return best_cost, match_x

impl Solution {
    pub fn maximum_and_sum(nums: Vec<i32>, num_slots: i32) -> i32 {
        let n = nums.len();
        let mut ans = 0;
        let mut dp = vec![-1; 1 << n];
        dp[0] = 0;
        for s in 1..=num_slots {
            for i in (0..(1<<n)).rev() {
                if dp[i] == -1 {
                    continue;
                }

                for j in 0..n {
                    if (i & (1 << j)) != 0 {
                        continue;
                    }

                    let msk = i ^ (1 << j);
                    dp[msk] = dp[msk].max(dp[i] + (nums[j] & s));

                    for k in j+1..n {
                        if (i & (1 << k)) != 0 {
                            continue;
                        }
                        dp[msk ^ (1 << k)] = dp[msk ^ (1 << k)].max(dp[i] + (nums[j] & s) + (nums[k] & s));
                    }
                }
            }
        }

        return dp[(1 << n) - 1];
    }
}

class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n = s.size();
        vector<vector<int>> freq(26, vector<int>(n + 1, 0));

        for (int c = 0; c < 26; c++)
            for (int i = 0; i < n; i++)
                freq[c][i + 1] = freq[c][i] + (s[i] == 'a' + c ? 1 : 0);

        vector<bool> answer;

        for (vector<int> &query : queries) {
            int left = query[0], right = query[1], k = query[2];
            right++;

            int odd_count = 0;

            for (int c = 0; c < 26; c++)
                if ((freq[c][right] - freq[c][left]) % 2 != 0)
                    odd_count++;

            answer.push_back(odd_count <= 2 * k + 1);
        }

        return answer;
    }
};

class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        int[][] cnts = new int[s.length() + 1][26];

        for (int i = 0; i < s.length(); i++) {
            for (int j = 0; j < 26; j++) {
                cnts[i + 1][j] = cnts[i][j];
            }
            cnts[i + 1][s.charAt(i) - 'a']++;
        }

        List<Boolean> ret = new ArrayList<>();
        for(int[] q : queries){
            int odd = 0;
            for (int i = 0; i < 26; i++) {
                int nums = cnts[q[1] + 1][i] - cnts[q[0]][i];
                odd += nums % 2;
            }

            if(odd - 2 * q[2] <= 1) ret.add(true);
            else ret.add(false);
        }
        return ret;
    }
}

class Solution:
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:
        qdict = [{}]
        curdict = {}
        for c in s:
            curdict[c] = curdict.get(c, 0) + 1
            qdict.append({k:v for k, v in curdict.items()})
        ret = []
        for left, right, k in queries:
            dff = 0
            for c, n in qdict[right + 1].items():
                if (n - qdict[left].get(c, 0)) % 2:
                    dff += 1
            ret.append(dff >> 1 <= k)
        return ret

