var canMakePaliQueries = function(s, queries) {
    const map = {}
    for(let i = 0; i < s.length; i++) {
        for(let j = 0; j < 26; j++) {
            const c = String.fromCharCode(97 + j)
            const qz = map[c] || []
            qz.push((qz[qz.length - 1] || 0))
            map[c] = qz
        }
        const c = s[i]
        map[c][map[c].length - 1]++
    }
    const ans = queries.map(i => {
        const [l, r, k] = i
        let cnt = 0
        for(let j = 0; j < 26; j++) {
            const c = String.fromCharCode(97 + j)
            const qz = map[c]
            const del = qz[r] - (qz[l - 1] || 0)
            if (del % 2 !== 0) cnt++
        }
        // console.log(cnt)
        if (~~(cnt/2) <= k) return true
        return false
    })

    return ans
};

