int gcs[100004][26];
bool* canMakePaliQueries(char * s, int** queries, int queriesSize, int* queriesColSize, int* returnSize){
  int n = queriesSize,l =strlen(s);
  int i, j;
  for (j=0; j<26; j++) gcs[0][j]=0;
  for (i=0; i<l; i++) {
    for (j=0; j<26; j++) gcs[i+1][j]=gcs[i][j];
    gcs[i+1][s[i]-'a']++;
  }
  int cs[26];
  int a, b, k, c;
  *returnSize = n;
  bool* r = (bool*) malloc(n*sizeof(bool));
  for (i=0; i<n; i++) {
    for (j=0; j<26; j++) cs[j]=0;
