    if (a>b) { k=a; a=b; b=k; }
    k=queries[i][2];
    for (j=0; j<26; j++) cs[j]=gcs[b+1][j]-gcs[a][j];
    c = 0; for (j=0; j<26; j++) if (cs[j]&1) c++;
    if ((b-a+1)&1) c--;
    c = (c+1)/2;
    if (c<=k) r[i]=true;
    else r[i]=false;
  }
  return r;
