    	for (int i = 1; i <= n; i++) {
    		for (int j = 1; j <= m; j++) {
                c[i][j] += c[i - 1][j] + c[i][j - 1] - c[i - 1][j - 1];
    			int v = c[i][j] + w[i][j];
    			if (!v) return 0;
    		}
    	}
    	return 1;
    }
};

class Solution:
    def possibleToStamp(self, grid: List[List[int]], p: int, q: int) -> bool:
        r, c = len(grid), len(grid[0])
        psa = [[0]*(c+1) for i in range(r+1)]
        for i in range(r):
            for j in range(c):
                psa[i+1][j+1] = psa[i][j+1]+psa[i+1][j]-psa[i][j]+grid[i][j]
        dif = [[0]*(c+1) for i in range(r+1)]
        for i in range(r-p+1):
            for j in range(c-q+1):
                rect = psa[i+p][j+q]-psa[i][j+q]-psa[i+p][j]+psa[i][j]
                if rect == 0:
                    #print('test', i, j)
                    dif[i][j] += 1
                    dif[i+p][j] -= 1
                    dif[i][j+q] -= 1
                    dif[i+p][j+q] +=1

        for i in range(r+1):
            for j in range(c):
                dif[i][j+1] += dif[i][j]
        for j in range(c+1):
            for i in range(r):
                dif[i+1][j] += dif[i][j]
        for i in range(r):
            for j in range(c):
                if grid[i][j]+dif[i][j]== 0:
                    return False
        return True

class Solution {

    private void build(int[][] grid , int height , int width) {
        // build count
        int i , j , n = grid.length , m = grid[0].length;
        for (i = 0;i < n;i ++) {
            count[i][0] = grid[i][0];
            if (i > 0) {
                count[i][0] += count[i - 1][0];
            }
        }
        for (j = 0;j < m;j ++) {
            count[0][j] = grid[0][j];
            if (j > 0) {
                count[0][j] += count[0][j - 1];
            }
        }
        for (i = 1;i < n;i ++) {
            for (j = 1;j < m;j ++) {
                count[i][j] = count[i - 1][j] + count[i][j - 1] - count[i - 1][j - 1] + grid[i][j];
            }
        }
        // build candidate
        for (i = 0;i < n;i ++) {
            for (j = 0;j < m;j ++) {
                if (grid[i][j] == 0 && i + height - 1 < n && j + width - 1 < m) {
                    int total = query(i , j , i + height - 1 , j + width - 1);
                    if (total == 0) {
                        candidate[i][j] = 1;
                    }
                }
            }
        }
        for (i = 0;i < n;i ++) {
            candidateSum[i][0] = candidate[i][0];
            if (i > 0) {
                candidateSum[i][0] += candidateSum[i - 1][0];
            }
        }
        for (j = 0;j < m;j ++) {
            candidateSum[0][j] = candidate[0][j];
            if (j > 0) {
                candidateSum[0][j] += candidateSum[0][j - 1];
            }
        }
        for (i = 1;i < n;i ++) {
            for (j = 1;j < m;j ++) {
                candidateSum[i][j] = candidateSum[i - 1][j] + candidateSum[i][j - 1] - candidateSum[i - 1][j - 1] + candidate[i][j];
            }
        }
    }

    private int query(int x1 , int y1 , int x2 , int y2) {
        int sum = count[x2][y2];
        if (x1 - 1 >= 0) {
            sum -= count[x1 - 1][y2];
        }
        if (y1 - 1 >= 0) {
            sum -= count[x2][y1 - 1];
        }
        if (x1 - 1 >= 0 && y1 - 1 >= 0) {
            sum += count[x1 - 1][y1 - 1];
        }
        return sum;
    }

    private boolean check(int x1 , int y1 , int x2 , int y2) {
        int sum = candidateSum[x2][y2];
        if (x1 - 1 >= 0) {
            sum -= candidateSum[x1 - 1][y2];
        }
        if (y1 - 1 >= 0) {
            sum -= candidateSum[x2][y1 - 1];
        }
        if (x1 - 1 >= 0 && y1 - 1 >= 0) {
            sum += candidateSum[x1 - 1][y1 - 1];
        }
        return sum > 0;
    }

    private int[][] count;
    private int[][] candidateSum;
    private int[][] candidate;

    public boolean possibleToStamp(int[][] grid, int stampHeight, int stampWidth) {
        int i , j , n = grid.length , m = grid[0].length;
        count = new int[n][m];
        candidate = new int[n][m];
        candidateSum = new int[n][m];
        build(grid , stampHeight , stampWidth);
        for (i = 0;i < n;i ++) {
            for (j = 0;j < m;j ++) {
                if (grid[i][j] == 0) {
                    int x = Math.max(0 , i - stampHeight + 1) , y = Math.max(0 , j - stampWidth + 1);
                    if (!check(x , y , i , j)) {
                        return false;
                    }
                }
            }
        }
        return true;
    }

}

class Solution(object):
    def possibleToStamp(self, M, h, w):
        m, n = len(M), len(M[0])
        A = [[0] * (n + 1) for _ in range(m + 1)]
        good = [[0] * n for _ in range(m)]
        for i in xrange(m):
            for j in xrange(n):
                A[i+1][j+1] = A[i+1][j] + A[i][j+1] - A[i][j] + (1 - M[i][j])
                if i + 1 >= h and j + 1 >= w:
                    x, y = i+1-h, j+1-w
                    if A[i+1][j+1] - A[x][j+1] - A[i+1][y] + A[x][y] == w*h:
                        good[i][j] += 1
        # for r in A: print r
        # for r in good: print r
        B = [[0] * (n + 1) for _ in range(m + 1)]
        for i in xrange(m):
            for j in xrange(n):
                B[i+1][j+1] = B[i+1][j] + B[i][j+1] - B[i][j] + good[i][j]
        for i in xrange(m):
            for j in xrange(n):
                if M[i][j] == 1:
                    continue
                x,y = min(i+h,m),min(j+w,n)
                if B[x][y] - B[i][y] - B[x][j] + B[i][j] == 0:
                    return False
        return True

/**
 * @param {number[][]} grid
 * @param {number} stampHeight
 * @param {number} stampWidth
 * @return {boolean}
 */
var possibleToStamp = function(grid, stampHeight, stampWidth) {
    let N = grid.length, M = grid[0].length
    if (N > M) {
        return possibleToStamp(rev(grid), stampWidth, stampHeight)
    }
    let colors = Array(N).fill(0).map(x => Array(M + 1).fill(0))
    let sums = Array(N + 1).fill(0).map(x => Array(M + 1).fill(0))
    for (let i = 0; i < N; i++) {
        for (let j = 0, rowSum = 0; j < M; j++) {
            rowSum += grid[i][j]
            sums[i + 1][j + 1] = sums[i][j + 1] + rowSum
        }
    }
    // console.log(sums)
    for (let i = 0; i < N; i++) {
        for (let j = 0; j < M; j++) {
            if (grid[i][j] === 0 && sum(i, j, i + stampHeight - 1, j + stampWidth - 1) === 0) {
                for (let k = i; k < i + stampHeight; k++) {
                    colors[k][j]++
                    colors[k][j + stampWidth]--
                }
            }
        }
    }
    for (let i = 0, color = 0; i < N; i++) {
        for (let j = 0; j <= M; j++) {
            color += colors[i][j]
            if (j === M) continue
            if (grid[i][j] === 0 && !color) return false
        }
    }
    return true

    function sum(x0, y0, x1, y1) {
        if (x1 + 1 > N || y1 + 1 > M) return 1
        return sums[x1 + 1][y1 + 1] - sums[x0][y1 + 1] - sums[x1 + 1][y0] + sums[x0][y0]
    }
    function rev(grid) {
        let ans = Array(M).fill(0).map(x => Array(N).fill(0))

        for (let i = 0; i < N; i++) {
            for (let j = 0; j < M; j++) {
                ans[j][i] = grid[i][j]
            }
        }
        return ans
    }
};

impl Solution {
    pub fn possible_to_stamp(mut grid: Vec<Vec<i32>>, stamp_height: i32, stamp_width: i32) -> bool {
        let stamp_height = stamp_height as usize;
        let stamp_width = stamp_width as usize;
        let base = grid.clone();
        // Convert to: has stamp_width free to right
        for row in &mut grid {
            let mut current = 0;
            for cell in row.iter_mut().rev() {
                current = match cell {
                    0 => current + 1,
                    1 => 0,
                    _ => unreachable!(),
                };
                *cell = if current >= stamp_width { 1 } else { 0 };
            }
        }
        // Convert to: has stamp_height*stamp_width free to bottom-right (aka "can stamp")
        for idx in 0..grid[0].len() {
            let mut current = 0;
            for cell in grid.iter_mut().map(|row| &mut row[idx]).rev() {
                current = match cell {
                    1 => current + 1,
                    0 => 0,
                    _ => unreachable!(),
                };
                *cell = if current >= stamp_height { 1 } else { 0 };
            }
        }
        // Convert to: stamped row starting from here
        for idx in 0..grid[0].len() {
            let mut current: usize = 0;
            for cell in grid.iter_mut().map(|row| &mut row[idx]) {
                current = match cell {
                    0 => current.saturating_sub(1),
                    1 => stamp_height,
                    _ => unreachable!(),
                };
                *cell = if current > 0 { 1 } else { 0 };
            }
        }
        // Convert to: stamped
        for row in &mut grid {
            let mut current: usize = 0;
            for cell in row.iter_mut() {
                current = match cell {
                    0 => current.saturating_sub(1),
                    1 => stamp_width,
                    _ => unreachable!(),
                };
                *cell = if current > 0 { 1 } else { 0 };
            }
        }
        grid.into_iter().zip(base)
            .all(|(gr, br)| gr.into_iter().zip(br).all(|(st, occ)| st + occ > 0))
    }
}

using T = map<string, int>;
class Solution {
  T f(string s) {
    if (s.empty()) return T();
    if (s[0] == '(') {
      int l = 1, i = 1;
      while (i < s.size() && l) {
        if (s[i] == '(') l++;
        else if (s[i] == ')') l--;
        i++;
      }
      T r = f(s.substr(1, i - 2));
      int b = 0;
      while (i < s.size() && isdigit(s[i])) {
        b = b * 10 + s[i++] - '0';
      }
      if (b == 0) b = 1;
      for (auto &it : r) it.second *= b;
      auto q = f(s.substr(i));
      for (auto &it : q) r[it.first] += it.second;
      return r;
    } else {
      T r;
      int i = 0;
      string t = "";
      t += s[i++];
      while (i < s.size() && islower(s[i]))
        t += s[i++];
      int b = 0;
      while (i < s.size() && isdigit(s[i])) {
        b = b * 10 + s[i++] - '0';
      }
      if (b == 0) b = 1;
      r[t] = b;
      auto q = f(s.substr(i));
      for (auto &it : q) r[it.first] += it.second;
      return r;
    }
  }
public:
  string countOfAtoms(string s) {
    T r = f(s);
    string ans = "";
    for (auto it : r) {
      ans += it.first;
      if (it.second > 1) ans += to_string(it.second);
    }
    return ans;
  }
};

class Solution {
	char[] s;
	int pos, len;

    public String countOfAtoms(String formula) {
        s = formula.toCharArray();
        pos = 0;
        len = s.length;
        TreeMap<String, java.math.BigInteger> res = go();
        StringBuilder sb = new StringBuilder();
        for(String key : res.keySet()){
        	sb.append(key);
        	java.math.BigInteger val = res.get(key);
        	if(!val.equals(java.math.BigInteger.ONE)){
        		sb.append(val);
        	}
        }
        return sb.toString();
    }

    TreeMap<String, java.math.BigInteger> go()
    {
    	TreeMap<String, java.math.BigInteger> ret = new TreeMap<>();
    	while(pos < len && s[pos] != ')'){
	    	if(s[pos] == '('){
	    		pos++;
	    		TreeMap<String, java.math.BigInteger> res = go();
	    		pos++;
	    		java.math.BigInteger num = num();
	    		for(String key : res.keySet()){
	    			java.math.BigInteger val = res.get(key).multiply(num);
	    			if(ret.containsKey(key)){
	    				ret.put(key, ret.get(key).add(val));
	    			}else{
	    				ret.put(key, val);
	    			}
	    		}
	    	}else{
	    		String atom = atom();
	    		java.math.BigInteger num = java.math.BigInteger.ONE;
	    		if(pos < len && s[pos] >= '0' && s[pos] <= '9'){
	    			num = num();
	    		}
    			if(ret.containsKey(atom)){
    				ret.put(atom, ret.get(atom).add(num));
    			}else{
    				ret.put(atom, num);
    			}
	    	}
    	}
    	return ret;
    }

    String atom()
    {
    	int opos = pos++;
    	while(pos < len && s[pos] >= 'a' && s[pos] <= 'z')pos++;
    	return new String(s, opos, pos - opos);
    }

    java.math.BigInteger num()
    {
    	int opos = pos;
    	while(pos < len && s[pos] >= '0' && s[pos] <= '9')pos++;
    	return new java.math.BigInteger(new String(s, opos, pos-opos));
    }
}

from collections import defaultdict

class Solution(object):
    def countOfAtoms(self, formula):
        """
        :type formula: str
        :rtype: str
        """
        def getDict(formula):
            cur = 0
            answer = defaultdict(lambda : 0)
            while cur < len(formula):
                if formula[cur] == '(':
                    i = cur + 1
                    depth = 1
                    while depth > 0:
                        if formula[i] == '(':
                            depth += 1
                        elif formula[i] == ')':
                            depth -= 1
                        i += 1
                    numIndex = i
                    while i < len(formula) and formula[i].isdigit():
                        i += 1
                    nextIndex = i
                    times = 1 if numIndex == nextIndex else int(formula[numIndex:nextIndex])
                    subDict = getDict(formula[cur + 1: numIndex - 1])
                    for k, v in subDict.items():
                        answer[k] += v * times
                    cur = nextIndex
                else:
                    i = cur + 1
                    while i < len(formula) and formula[i].islower():
                        i += 1
                    numIndex = i
                    while i < len(formula) and formula[i].isdigit():
                        i += 1
                    nextIndex = i
                    times = 1 if numIndex == nextIndex else int(formula[numIndex:nextIndex])
                    element = formula[cur:numIndex]
                    answer[element] += times
                    cur = nextIndex
            return answer
        d = getDict(formula)
        items = sorted(d.items())
        answer = []
        for k, v in items:
            answer.append(k)
            if v > 1:
                answer.append(str(v))
        return ''.join(answer)


