import re


class Solution:
    def countOfAtoms(self, formula):
        """
        :type formula: str
        :rtype: str
        """

        def add(a, b):
            c = {}
            keys = set(a.keys()) | set(b.keys())
            for k in keys:
                c[k] = a.get(k, 0) + b.get(k, 0)
            return c

        def mul(a, t):
            return {k: t * v for k, v in a.items()}

        def count(f):
            if not f:
                return dict()
            p = f.find('(')
            if p == -1:
                fs = []
                for s in re.split('([A-Z][a-z]?)', f):
                    if s:
                        fs.append(s)
                ans = {}
                for i, s in enumerate(fs):
                    if s[0] in '1234567890':
                        continue
                    c = 1
                    if i + 1 < len(fs) and fs[i + 1][0] in '1234567890':
                        c = int(fs[i + 1])
                    ans[s] = ans.get(s, 0) + c
                return ans
            c = 1
            q = p + 1
            while q < len(f) and c != 0:
                if f[q] == '(':
                    c += 1
                elif f[q] == ')':
                    c -= 1
                q += 1
            f1 = f[:p]
            f2 = f[p + 1:q - 1]
            t = 1
            if q < len(f) and f[q] in '1234567890':
                c = ''
                while q < len(f) and f[q] in '1234567890':
                    c += f[q]
                    q += 1
                t = int(c)
            f3 = f[q:]
            ans = mul(count(f2), t)
            ans = add(count(f1), ans)
            ans = add(count(f3), ans)
            return ans

        atoms = count(formula)
        atoms = sorted(atoms.items())
        ans = ''
        for k, v in atoms:
            ans += k
            if v > 1:
                ans += str(v)
        return ans

class Solution {
    public int countSpecialNumbers(int n) {
        return n - numDupDigitsAtMostN(n);
    }
    //repeated question (solved before)
    //https://leetcode.com/problems/numbers-with-repeated-digits/
    //https://leetcode.com/problems/numbers-with-repeated-digits/discuss/256725/JavaPython-Count-the-Number-Without-Repeated-Digit
    //from lee215
     public int numDupDigitsAtMostN(int N) {
        // Transform N + 1 to arrayList
        ArrayList<Integer> L = new ArrayList<Integer>();
        for (int x = N + 1; x > 0; x /= 10)
            L.add(0, x % 10);

        // Count the number with digits < N
        int res = 0, n = L.size();
        for (int i = 1; i < n; ++i)
            res += 9 * A(9, i - 1);

        // Count the number with same prefix
        HashSet<Integer> seen = new HashSet<>();
        for (int i = 0; i < n; ++i) {
            for (int j = i > 0 ? 0 : 1; j < L.get(i); ++j)
                if (!seen.contains(j))
                    res += A(9 - i, n - i - 1);
            if (seen.contains(L.get(i))) break;
            seen.add(L.get(i));
        }
        return N - res;
    }


    public int A(int m, int n) {
        return n == 0 ? 1 : A(m, n - 1) * (m - n + 1);
    }
}

