class Solution {
public:
    int countSpecialNumbers(int n){
        memset(dp,0,sizeof(dp));
        vector<int> vec;
        vec.clear();
        while(n){
            vec.push_back(n%10);
            n/=10;
        }
        int len=vec.size();
        for(int i=len-1;i>=0;i--){
            for(int x=1;x<=((i==len-1)?vec[i]:9);x++) dp[i][1<<x][(i==len-1)&&(x==vec[i])]++;
            for(int l=0;l<=1;l++){
                for(int x=0;x<=(l?vec[i]:9);x++){
                    for(int mask=0;mask<(1<<10);mask++){
                            if((mask>>x)&1) continue;
                            dp[i][mask|(1<<x)][l&&(x==vec[i])]+=dp[i+1][mask][l];
                    }
                }
            }
        }
        int res=0;
        for(int mask=1;mask<(1<<10);mask++) res+=dp[0][mask][0]+dp[0][mask][1];
        return res;
    }
};

class Solution(object):
    def countSpecialNumbers(self, n):
        """
        :type n: int
        :rtype: int
        """
        def c(n, m):
            r = 1
            for i in range(n):
                r, m = r * m, m - 1
            return r
        def e(i, v, j):
            if i == len(str(n)):
                return 1
            elif not j:
                return c(len(str(n)) - i, 10 - i)
            r = 0
            for k in range(0 if i > 0 else 1, ord(str(n)[i]) - ord('0') + 1):
                if not v[k]:
                    v[k] = True
                    r, v[k] = r + e(i + 1, v, k == ord(str(n)[i]) - ord('0')), False
            return r
        r = 0
        for i in range(0, len(str(n)) - 1):
            r += 9 * c(i, 9)
        r += e(0, [False] * 10, True)
        return r

