    let t = x;
    // 
    const num = new Array();
    while (t) {
        num.push(t % 10);
        t = Math.floor(t / 10);
    }
    let n = num.length;
    if (n <= 1) return x + 1; // [0, 9]
    // :
    // res1: x, x
    // res2: x, 
    // res3: x

    // x(res1 + res2)
    let res = 0;
    // i:, p:, s: , 
    for (let i = n - 1, p = 1, s = 0; i >= 0; i -- , p ++ ) { 
        let cur = num[i], cnt = 0; // , 
        for (let j = cur - 1; j >= 0; j -- ) {
            if (i === n - 1 && j === 0) continue; // 0
            if (((s >> j) & 1) === 0) cnt ++ ; // 
        }
        // kcnt, n - k
        let a = 10 - p, b = (10 - p) - (n - p) + 1; // n - p, a * (a-1) * ... * b;
        // f[b][a]: 
        // cnt * f[b][a] 
        // f[b][a] 
        res += (b <= a ? cnt * f[b][a] : cnt);
        if (((s >> cur) & 1) == 1) break; // 
        s |= (1 << cur); // 
        if (i == 0) res ++ ; // , 
    }
    // x(res3)
    // 12...
    // 
    // 1 1+9
    // 2 1+9+9*9
    // 3 1+9+9*9+9*9*8
    res += 10;
    for (let i = 2, last = 9; i < n; i ++ ) {
        let cur = last * (10 - i + 1);
        res += cur; last = cur;
    }
    return res;
