            for d in 0..10 {
                if used & (1 << d) == 0 {
                    backtrack(num * 10 + d, max, used | (1 << d), ret);
                }
            }
        }

        let mut ret = 0;
        for first in 1..10 {
            backtrack(first, n as i64, 1 << first, &mut ret);
        }
        ret
    }
}

