        d = defaultdict(int)
        for w in words:
            d[w] += 1
        ret = 0
        odd = False
        for w in list(d):
            r = w[::-1]
            if w == r:
                x = d[w] // 2
            else:    
                x = min(d[w], d[r])
            d[w] -= x
