class Solution {


    public int longestPalindrome(String[] words) {
        Map<String , Integer> cntMap = new HashMap<>();
        for (String word : words) {
            cntMap.put(word , cntMap.getOrDefault(word , 0) + 1);
        }
        boolean odd = false;
        int ans = 0;
        List<String> keys = new ArrayList<>(cntMap.keySet());
        for (String key : keys) {
            if (cntMap.containsKey(key)) {
                if (key.charAt(0) == key.charAt(1)) {
                    int cnt = cntMap.get(key);
                    ans += (cnt / 2 * 2 * 2);
                    if (cnt % 2 == 1) {
                        odd = true;
                    }
                } else {
                    String anotherKey = key.charAt(1) + "" + key.charAt(0);
                    int cnt1 = cntMap.get(key) , cnt2 = cntMap.getOrDefault(anotherKey , 0);
                    int cnt = Math.min(cnt1 , cnt2);
                    ans += (cnt * 2 * 2);
                    cntMap.remove(key);
                    cntMap.remove(anotherKey);
                }
            }
        }
        if (odd) {
            ans += 2;
        }
        return ans;
    }

}

