use std::collections::HashMap;
impl Solution {
    pub fn longest_palindrome(words: Vec<String>) -> i32 {
        let mut distinct = HashMap::new();
        let mut same = HashMap::new();
        for word in words {
            let a = word.as_bytes()[0];
            let b = word.as_bytes()[1];
            if a == b {
                *same.entry(a).or_insert(0) += 1;
            } else {
                let mut entry = distinct.entry((std::cmp::min(a, b), std::cmp::max(a, b)))
                    .or_insert((0, 0));
                if a > b {
                    entry.0 += 1;
                } else {
                    entry.1 += 1;
                }
            }
        }
        let distinct: i32 = distinct.values()
            .map(|&(l, r)| std::cmp::min(l, r))
            .sum();
        let has_center = same.values().any(|&v| v % 2 == 1);
        let same: i32 = same.values().map(|&v| v / 2).sum();
        (distinct * 2 + same * 2 + if has_center { 1 } else { 0 }) * 2
    }
}

