        def f(i):
            a = (1 if len(s) else 0) if i == len(nums) else 2 * f(i + 1) if nums[i] in s else f(i + 1)
            if i != len(nums) and not nums[i] in s and not nums[i] - k in s and not nums[i] + k in s:
                s.add(nums[i])
                a += f(i + 1)
                s.remove(nums[i])
            return a
        return f(0)

