var beautifulSubsets =function(nums, k) {
    const N = nums.length
    let limit = 1 << N
    let ans = 0
    l1: for (let bset = 1; bset < limit; bset++) {
        let set = new Set()
        for (let i = 0; i < N; i++) {
            let bit = 1 << i
            let num = nums[i]
            if (bset & bit) {
                if (set.has(num - k) || set.has(num + k)) continue l1
                set.add(num)
            }
        }
        ans++
    }
    return ans
};

