impl Solution {
    pub fn beautiful_subsets(nums: Vec<i32>, k: i32) -> i32 {
        let mut set = HashSet::new();
        let mut ans = 0;
        Self::brutal(&nums, set, k, &mut ans);
        ans - 1
    }

    fn brutal(nums: &[i32], mut set: HashSet<i32>, k: i32, ans: &mut i32) {
        if nums.is_empty() {
            *ans += 1;
            return;
        }
        let v = nums[0];

        Self::brutal(&nums[1..], set.clone(), k, ans);
        if !set.contains(&(v - k)) && !set.contains(&(v + k)) {
            set.insert(v);
            Self::brutal(&nums[1..], set, k, ans);
        }
    }
}

