class Solution {
	public int[] minDifference(int[] nums, int[][] queries) {
		int n = nums.length;
		LST[] lst = new LST[101];
		for(int i = 1;i <= 100;i++){
			lst[i] = new LST(n);
		}
		for(int i = 0;i < n;i++){
			lst[nums[i]].set(i);
		}
		int[] ret = new int[queries.length];
		boolean[] ex = new boolean[101];
		for(int i = 0;i < ret.length;i++){
			int[] q = queries[i];
			int pr = -1;
			int ans = 9999999;
			for(int j = 1;j <= 100;j++){
				int ne = lst[j].next(q[0]);
				if(ne != -1 && ne <= q[1]){
					if(pr != -1){
						ans = Math.min(ans, j - pr);
					}
					pr = j;
				}
			}
			if(ans == 9999999)ans = -1;
			ret[i] = ans;
		}
		return ret;
	}

	public class LST {
		public long[][] set;
		public int n;
		//	public int size;

		public LST(int n) {
			this.n = n;
			int d = 1;
			for(int m = n;m > 1;m>>>=6, d++);

			set = new long[d][];
			for(int i = 0, m = n>>>6;i < d;i++, m>>>=6){
				set[i] = new long[m+1];
			}
			//		size = 0;
		}

		// [0,r)
		public LST setRange(int r)
		{
			for(int i = 0;i < set.length;i++, r=r+63>>>6){
				for(int j = 0;j < r>>>6;j++){
					set[i][j] = -1L;
				}
				if((r&63) != 0)set[i][r>>>6] |= (1L<<r)-1;
			}
			return this;
		}

		// [0,r)
		public LST unsetRange(int r)
		{
			if(r >= 0){
				for(int i = 0;i < set.length;i++, r=r+63>>>6){
					for(int j = 0;j < r+63>>>6;j++){
						set[i][j] = 0;
					}
					if((r&63) != 0)set[i][r>>>6] &= ~((1L<<r)-1);
				}
			}
			return this;
		}

		public LST set(int pos)
		{
			if(pos >= 0 && pos < n){
				//			if(!get(pos))size++;
				for(int i = 0;i < set.length;i++, pos>>>=6){
					set[i][pos>>>6] |= 1L<<pos;
				}
			}
			return this;
		}

		public LST unset(int pos)
		{
			if(pos >= 0 && pos < n){
				//			if(get(pos))size--;
				for(int i = 0;i < set.length && (i == 0 || set[i-1][pos] == 0L);i++, pos>>>=6){
					set[i][pos>>>6] &= ~(1L<<pos);
				}
			}
			return this;
		}

		public boolean get(int pos)
		{
			return pos >= 0 && pos < n && set[0][pos>>>6]<<~pos<0;
		}

		public LST toggle(int pos)
		{
			return get(pos) ? unset(pos) : set(pos);
		}

		public int prev(int pos)
		{
			for(int i = 0;i < set.length && pos >= 0;i++, pos>>>=6, pos--){
				int pre = prev(set[i][pos>>>6], pos&63);
				if(pre != -1){
					pos = pos>>>6<<6|pre;
					while(i > 0)pos = pos<<6|63-Long.numberOfLeadingZeros(set[--i][pos]);
					return pos;
				}
			}
			return -1;
		}

		public int next(int pos)
		{
			for(int i = 0;i < set.length && pos>>>6 < set[i].length;i++, pos>>>=6, pos++){
				int nex = next(set[i][pos>>>6], pos&63);
				if(nex != -1){
					pos = pos>>>6<<6|nex;
					while(i > 0)pos = pos<<6|Long.numberOfTrailingZeros(set[--i][pos]);
					return pos;
				}
			}
			return -1;
		}

		private int prev(long set, int n)
		{
			long h = set<<~n;
			if(h == 0L)return -1;
			return -Long.numberOfLeadingZeros(h)+n;
		}

		private int next(long set, int n)
		{
			long h = set>>>n;
			if(h == 0L)return -1;
			return Long.numberOfTrailingZeros(h)+n;
		}

		@Override
		public String toString()
		{
			List<Integer> list = new ArrayList<Integer>();
			for(int pos = next(0);pos != -1;pos = next(pos+1)){
				list.add(pos);
			}
			return list.toString();
		}
	}
}

class Solution:
    def minDifference(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        from bisect import bisect_left
        gg = defaultdict(list)
        for i,x in enumerate(nums):
            gg[x].append(i)
        rv = []
        for l,r in queries:
            valid = []
            for i in range(1,101):
                j = bisect_left(gg[i],l)
                if j>=len(gg[i]) or gg[i][j]>r:
                    continue 
                else:
                    valid.append(i) 
            if len(valid)<=1:
                rv.append(-1)
                continue
            v = 101
            for i in range(len(valid)-1):
                v = min(v,valid[i+1]-valid[i])
            rv.append(v)
        return rv

