class Solution {
public:
    int minDistance(string a, string b) {
        const static int N=514;
        static int dp[N][N];
        int n=a.size(),m=b.size();
        memset(dp,0,sizeof(dp));
        for ( int i=0; i<n; i++ ) for ( int j=0; j<m; j++ ) {
            if ( a[i]==b[j] ) dp[i+1][j+1]=dp[i][j]+1;
