public class Solution {
    public int minDistance(String word1, String word2) {
    	if(word1 == null && word2 == null)return 0;
    	if(word1 == null)return word2.length();
    	if(word2 == null)return word1.length();
        return editDistance(word1.toCharArray(), word2.toCharArray(), 1, 1, 999999);
    }
    
	public int editDistance(char[] a, char[] b, int add, int erase, int change)
	{
		int INF = Integer.MAX_VALUE / 3;
		int n = a.length;
		int m = b.length;
		int[][] dp = new int[n+1][m+1];
		for(int i = 0;i <= n;i++){
			for(int j = 0;j <= m;j++){
				if(i == 0 && j == 0)continue;
				int min = INF;
				if(i-1 >= 0)min = Math.min(min, dp[i-1][j] + erase);
				if(j-1 >= 0)min = Math.min(min, dp[i][j-1] + add);
				if(i-1 >= 0 && j-1 >= 0)min = Math.min(min, dp[i-1][j-1] + (a[i-1] != b[j-1] ? change : 0));
				dp[i][j] = min;
			}
		}
		return dp[n][m];
	}
}

class Solution(object):
    def minDistance(self, A, B):
        memo = {}
        def dp(i, j):
            #min steps to make A[i:] and B[j:] the same
            if i == len(A) or j == len(B):
                return len(A) - i + len(B) - j
            if (i,j ) not in memo:
                if A[i] == B[j]:
                    ans= dp(i+1,j+1)
                else:
                    ans= 1 + min(dp(i+1, j), dp(i, j+1))
                memo[i,j]= ans
            return memo[i,j]
        return dp(0, 0)

class Solution:

    def minDistance(self, word1, word2):
        """
        :type word1: str
        :type word2: str
        :rtype: int
        """
        def LCS_(u, v):
            LCS = [[0 for i in range(len(v)+1)] for j in range(len(u)+1)]
            for c in range(len(v)-1,-1,-1):
                for r in range(len(u)-1,-1,-1):
                    if u[r] == v[c]:
                        LCS[r][c] = 1 + LCS[r+1][c+1]
                    else:
                        LCS[r][c] = max(LCS[r][c+1], LCS[r+1][c])
            return(LCS[0][0])
        l1,l2 = len(word1),len(word2)
        return(l1+l2-2*LCS_(word1,word2))

