struct BIT{
  vector<int> bit;
  int n;
  //1-indexed
  BIT(){init(-1);}
  BIT(int n_){init(n_);}
  void init(int n_){
    n=n_;
    bit.clear();
    bit.resize(n+1,0);
  }
  int sum(int i){
    int s=0;
    while(i>0){
      s+=bit[i];
      i-=i&-i;
    }
    return s;
  }
  void add(int i,int x){
    if(i==0) return;
    while(i<=n){
      bit[i]+=x;
      i+=i&-i;
    }
  }
  int sum0(int i){
    return sum(i+1);
  }
  void add0(int i,int x){
    add(i+1,x);
  }
};


class Solution {
public:
  string minInteger(string s, int k) {
    int n=s.size();
    string ans;
    BIT bit(n+100);
    queue<int> p[10];
    for(int i=0;i<n;i++)
      p[s[i]-'0'].push(i);

    for(int i=0;i<n;i++){
      for(int j=0;j<10;j++){
        if(p[j].empty()) continue;
        char c='0'+j;
        int e=p[j].front();
        int x=e+bit.sum0(e);
        if(x<=k){
          p[c-'0'].pop();
          bit.add0(e,-1);
          k-=x;
          ans.push_back(c);
          break;
        }
      }
    }
    return ans;
  }
};

