var minInteger = function(num, k) {
    let numArr = num.split('')
    let len = num.length
    let count = 0
    while(k&&count<len){
        let min = count
        for(let i=count;i<len&&i-count<=k;i++){
            if(numArr[i]<numArr[min]){
                min = i
            }
            if(numArr[min]==0)break
        }
        numArr.splice(count, 0,numArr[min])
        numArr.splice(min+1,1)
        k = k-(min-count)
        count++
    }
    return numArr.join('')
};

class Solution:
    def minInteger(self, num: str, k: int) -> str:
        if sorted(num) == num:
            return num

        n = len(num)
        if k >= n * (n - 1) // 2:
            return ''.join(sorted(num))

        num = list(num)
        i = 0
        while k and i < n:
            mini = min(num[i:i + k + 1])
            ind = i + num[i:i + k + 1].index(mini)
            if ind > i:
                k -= ind - i
                num[i:ind + 1] = [mini] + num[i:ind]
            i += 1
        return ''.join(num)

