def parse(x):
    i, n, r = 0, "", ""
    s = 1
    for c in x:
        if s == 1 and c.isdigit():
            i = i * 10 + int(c)
        elif s == 1 and c == '.':
            s = 2
        elif s == 2 and c.isdigit():
            n += c
        elif s == 2 and c == '(':
            s = 3
        elif s == 3 and c.isdigit():
            r += c
        else:
            pass
    if not n and not r:
        return Fraction(i, 1)
    if not r:
        return Fraction(int(str(i) + n), 10 ** len(n))
    return Fraction(int(str(i) + n + r) - int(str(i) + n), 10 ** (len(n) + len(r)) - 10 ** len(n))

