var isRationalEqual = function(S, T) {
    const gcd = (a, b) => {
        let c = a % b;
        while (c > 0) {
            a = b;
            b = c;
            c = a % b;
        }
        return b;
    };
    const add = ([a, b], [c, d]) => {
        const i = gcd(b, d);
        return [a*(d/i) + c*(b/i), b*(d/i)];
    };
    const simp = ([a, b]) => {
        const i = gcd(a, b);
        return [a/i, b/i];
    }
    const toR = s => {
        const match = s.match(/([0-9]+)(?:\.([0-9]*))?(?:\(([0-9]+)\))?/);
        const int = Number(match[1]);
        let q = [int, 1];
        if (match[2] === undefined) {
            return q;
        } 
        const fix = Number(match[2]), div = Number('1' + Array(match[2].length).fill('0').join(''));
        q = add(q, [fix, div]);
        if (match[3] === undefined) {
            return q;
        }
        const rep = Number(match[3]), divrep = div * Number(Array(match[3].length).fill('9').join(''));
        q = add(q, [rep, divrep]);
        return q;
    };
    const s = simp(toR(S)), t = simp(toR(T));
    console.log(s, t)
    return s[0] === t[0] && s[1] === t[1];
};

int convert(char* s, char* ss) {
    memset(ss, 0, 200);
    int i = 0, res = 0, idx = 0;
    for (; i < strlen(s); ++i) {
        if (s[i] == '(') break;
        if (s[i] == '.') {res = idx; continue;}
        ss[idx++] = s[i] - '0';
    }
    char *stt = strchr(s, '(');
    char *end = strchr(s, ')');
    while (stt != NULL) {
        for (char* c = stt + 1; c < end; ++c) {
            ss[idx++] = *c - '0';
            if (idx == 100) break;
        }
        if (idx == 100) break;
    }
    ss[idx] = 0;    
    if (res == 0) res = idx;
    return res;
}



