    let ans = 0;
    for (let i = 0; i < m; ++i) {
        for (let j = 0; j < n; ++j) {
            if (matrix[i][j] == 1) {
                a[j] += 1;
            } else {
                a[j] = 0;
            }
        }
        let b = [...a];
        b.sort((x, y) => y - x);
        for (let j = 0; j < b.length; ++j) {
            ans = Math.max(ans, b[j] * (j + 1));
        }
    }
    return ans;
};

class Solution {
public:
    int longestWPI(vector<int>& hours) {
        int n = hours.size();

        for (int i = 0; i < n; i++)
            hours[i] = hours[i] > 8 ? +1 : -1;

        vector<int> sums(n + 1, 0);

        for (int i = 0; i < n; i++)
            sums[i + 1] = sums[i] + hours[i];

        int best = 0;

        for (int i = 0; i < n; i++)
            for (int j = n; j > i && j - i > best; j--)
                if (sums[i] < sums[j])
                    best = j - i;

        return best;
    }
};

class Solution:
    def longestWPI(self, hours: List[int]) -> int:
        hours = [1 if h > 8 else -1 for h in hours]
        s = 0
        ans = 0
        memo = {0 : -1}
        for i, v in enumerate(hours):
            s += v
            if s > 0:
                ans = i + 1
            if s not in memo:
                memo[s] = i
            if (s - 1) in memo:
                ans = max(ans, i - memo[s - 1])
        return ans

class Solution {
