var longestWPI = function(hours) {
    let tireds = hours.map(item=>item>8?true:false);
    let tiredsDays = new Array(hours.length+1).fill(0);
    let normalDays = new Array(hours.length+1).fill(0);
    let _tireds = 0;
    let _normal = 0;
    for(let i=0;i<tireds.length;i++){
        tireds[i]?_tireds++:_normal++
        tiredsDays[i+1]=_tireds;
        normalDays[i+1]=_normal;
    }
    // console.log(tiredsDays,normalDays)
    let ans = 0;
    for(let i=1;i<=hours.length;i++){
        for(let j=1;j<=i;j++){
            if(tiredsDays[i]-tiredsDays[j-1]>normalDays[i]-normalDays[j-1]){
                ans = Math.max(i-j+1,ans)
            }
        }
    }
    return ans;
};

int longestWPI(int* hours, int hoursSize){
    int i, n=hoursSize, j, clc, chc;
    int mc=0, hc=0, lc=0, cc=0;
    for (i=0; i<n; i++) {
        if (hours[i]>8) hc++;
        else lc++;
    }
    for (i=0; i<n; i++) {
        if (n-i<=mc) break;
        clc=lc; chc=hc;
        if (clc<chc) {
            cc = n-i;
            if (mc<cc) mc=cc;
        }
        for (j=n-1; j>=i; j--) {
            if (hours[j]>8) chc--;
            else clc--;
            if (clc<chc) {
                cc = j-i;
                if (mc<cc) mc=cc;
                break;
            }
        }
        if (hours[i]>8) hc--;
        else lc--;
    }
    return mc;
}

