class Solution {
    const int P=1000000007;
public:
    int sumSubarrayMins(vector<int>& a) {
        int n=a.size();
        vector<int> l(n,-1),r(n,n),st;
        for(int i=0;i<n;i++){
            while(!st.empty()&&a[st.back()]>a[i])st.pop_back();
            if(!st.empty())l[i]=st.back();
            st.push_back(i);
        }
        st.clear();
        for(int i=n-1;i>=0;i--){
            while(!st.empty()&&a[st.back()]>=a[i])st.pop_back();
            if(!st.empty())r[i]=st.back();
            st.push_back(i);
        }
        int ans=0;
        for(int i=0;i<n;i++){
            int d=r[i]-l[i]-1;
            ans=(ll(ans)+ll(i-l[i])*ll(r[i]-i)*ll(a[i]))%P;
        }
        return ans;
    }
};

class Solution:
    def sumSubarrayMins(self, A):
        res = 0
        mo = int(10**9+7)
        ret1 = self.getList(A, lambda x,y: x > y)
        ret2 = self.getList(list(reversed(A)), lambda x,y: x >= y)
        # print(ret1, ret2)
        for i, a, i1, i2 in zip(range(len(A)), A, ret1, reversed(ret2)):
            res += (i1-i)*(i-(len(A)-1-i2))*a
            res %= mo
        return res

    def getList(self, A, cmp):
        stk = []
        ret = [-1] * len(A);
        for i, a in enumerate(A):
            while stk and cmp(stk[-1][0], a):
                ret[stk.pop()[1]] = i
            stk.append((a,i))
        while stk:
            ret[stk.pop()[1]] = len(A);
        return ret

class Solution {
    public int sumSubarrayMins(int[] a) {
        long ans = 0;
        LinkedList<Integer> nextMax = new LinkedList<>();
        int [] right = new int [a.length];
        for(int s = a.length - 1 ; s >= 0 ; s--) {
            while(nextMax.size() > 0 && a[nextMax.getLast()] >= a[s]) {
                nextMax.removeLast();
            }
            right[s] = (nextMax.size() == 0 ? a.length : nextMax.getLast()) - s;
            nextMax.add(s); 
        }
        nextMax.clear();

        for(int s = 0 ; s < a.length ; s++) {
            while(nextMax.size() > 0 && a[nextMax.getLast()] > a[s]) {
                nextMax.removeLast();
            }
            int leftLength = s - (nextMax.size() == 0 ? -1 : nextMax.getLast()) ;
            ans += (leftLength * right[s] * a[s]) % 1_000_000_007;
            ans %= 1_000_000_007;
            nextMax.add(s);    
        }

        return (int)ans;
    }
}

/**
 * @param {number[]} A
 * @return {number}
 */
var sumSubarrayMins = function(A) {
  let MOD = 10 ** 9 + 7
  A = [0].concat(A)
  let N = A.length
  function findIdx(i, j) {
    let p = i
    for (let k = i; k <= j; k += 1) if (A[k] < A[p]) p = k
    return p
  }
  function gao(i, j) {
    if (i > j) return 0
    if (i == j)  return A[i]
    let p = findIdx(i, j)
    let ret = (A[p] * (p - i + 1) % MOD * (j + 1 - p) % MOD + gao(i, p - 1) + gao(p + 1, j)) % MOD 
    return ret
  }
  return gao(1, N - 1)
}

