	class Solution {
		public int[][] validArrangement(int[][] pairs) {
			Map<Integer, Queue<Edge>> g = new HashMap<>();
			for(int[] pair : pairs) {
				g.computeIfAbsent(pair[0], k -> new ArrayDeque<>()).add(new Edge(pair[0], pair[1]));
			}
			List<Edge> route = eulerianPathDirected(g);

			int n = route.size();
			int[][] ans = new int[n][2];
			for(int i = 0; i < n; i++) {
				ans[n-1-i][0] = route.get(i).u;
				ans[n-1-i][1] = route.get(i).v;
			}
			return ans;
		}

		public List<Edge> eulerianPathDirected(Map<Integer, Queue<Edge>> g)
		{
//			int first = -1;
//			int odd = 0;
//			for(int key : g.keySet()) {
//				if(g.get(key).size() % 2 == 1) {
//					first = key;
//					odd++;
//				}
//			}
//			if(odd > 2)return null;
//			if(first == -1) {
//				for(int key : g.keySet()) first = key;
//			}
//			int m = 0;
//			for(Queue<Edge> es : g.values())m += es.size();
//			m /= 2;
			int first = -1;
			Map<Integer, Integer> deg = new HashMap<>();
			for(Queue<Edge> q : g.values()){
				for(Edge e : q){
					deg.merge(e.u, 1, Integer::sum);
					deg.merge(e.v, -1, Integer::sum);
				}
			}
			for(int e : deg.keySet()){
				if(deg.get(e) > 0){
					first = e;
				}
			}
			if(first == -1){
				for(int k : g.keySet()){
					first = k;
					break;
				}
			}

			List<Edge> route = new ArrayList<>();
			dfs(first, g, route);
			return route;
		}

		public class Edge
		{
			int u, v;
			public Edge(int u, int v) {
				this.u = u;
				this.v = v;
			}
		}

		private void dfs(int cur, Map<Integer, Queue<Edge>> rem, List<Edge> route)
		{
			while(rem.get(cur) != null && !rem.get(cur).isEmpty()) {
				Edge e = rem.get(cur).poll();
				dfs(e.v, rem, route);
				route.add(e);
			}
		}

	}

class Solution:
    def validArrangement(self, pairs: List[List[int]]) -> List[List[int]]:
        d = defaultdict(list)
        ins = defaultdict(int)
        # visited = set()
        for s, e in pairs:
            # visited.add(s)
            # visited.add(e)
            ins[s] += 1
            ins[e] -= 1
            d[s].append(e)
        queue = deque()
        def dfs(i):
            while d[i]:
                dfs(d[i].pop())
            queue.appendleft(i)
        pins = []
        for k, v in ins.items():
            if v == 1:
                pins.append(k)
        if not pins:
            # dfs(list(visited)[0])
            dfs(pairs[0][0])
        else:
            dfs(pins[0])
        res = []
        for i in range(1, len(queue)):
            res.append([queue[i - 1], queue[i]])
        return res

/**
 * @param {number[][]} pairs
 * @return {number[][]}
 */
var validArrangement = function(pairs) {
    const sc = {};
    const ec = {};
    const sp = {};
    const sci = {};
    for (const [s, e] of pairs) {
        sc[s] = (sc[s] || 0) + 1;
        ec[e] = (ec[e] || 0) + 1;
        if (!sp[s]) sp[s] = [];
        sp[s].push([s, e]);
    }
    const keys = Object.keys(sc);
    let first = keys[0];
    for (let i = 1; i < keys.length; i++) {
        if (sc[keys[i]] > ec[keys[i]] || !ec[keys[i]]) first = keys[i];
    }
    const result = [];
    const dfs = (x) => {
        if (!sci[x]) sci[x] = 0;
        while (sp[x] && sci[x] < sp[x].length) {
            const cur = sp[x][sci[x]];
            sci[x]++;
            dfs(cur[1]);
            result.push(cur);
        }
    }
    dfs(first);
    return result.reverse();
};

