    long num()
    {
    	long x = 0;
    	boolean sig = false;
    	if(pos < len && s[pos] == '-'){
    		sig = true;
    		pos++;
    	}
    	while(pos < len && s[pos] >= '0' && s[pos] <= '9'){
    		x = x * 10 + s[pos] - '0';
    		pos++;
    	}
    	return sig ? -x : x;
    }

    String var()
