class Solution {
public:
    vector<string> basicCalculatorIV(string expression, vector<string>& evalvars, vector<int>& evalints) {
        unordered_map<string, int> env;
        for (int i = 0; i <evalvars.size(); ++i)
            env[evalvars[i]] = evalints[i];
        auto res = solve(expression, 0, expression.size()-1, env);
        vector<pair<string, int>> coef;
        for (auto p: res)
            coef.push_back(p);

        auto cmp = [this](const pair<string, int> &a, const pair<string, int> &b) {
            if (a.first == "") return false;
            else if (b.first == "") return true;

            auto x = this->degree(a.first);
            auto y = this->degree(b.first);

            if (x != y) return x > y;
            return a.first < b.first;
        };

        sort(coef.begin(), coef.end(), cmp);

        vector<string> ans;        
        for (auto p: coef) {
            if (p.second == 0) continue;
            if (p.first == "")
                ans.push_back(to_string(p.second));
            else {
                ans.push_back(to_string(p.second)+"*"+p.first);
            };            
        };
        return ans;


    }

    map<string, int> solve(string &expr, int left, int right, unordered_map<string, int> &env) {
        map<string, int> ret;   
        ret[""] = 0;

        if (left > right) return ret;
        int i = left;
        int cur = 0;

        int pos = -1;
        char op;

        while (i <= right) {
            if (expr[i] == '(') 
                cur++;
            else if (expr[i] == ')')
                cur--;            
            else if (expr[i] == '*' || expr[i] == '+' || expr[i] == '-') {
                if (cur == 0) {
                    if (pos == -1) {
                        pos = i;
                        op = expr[i];
                    }
                    else if (expr[i] == '+' || expr[i] == '-') {
                        pos = i;
                        op = expr[i];
                    }
                    else if (op == '*' && expr[i] == '*') {
                        op = expr[i];
                        pos = i;
                    };                                       
                };
            }
            ++i;
        };

        if (pos == -1) {
            bool has_bracket = false;
            for (int i = left; i <= right; ++i) 
                if (expr[i] == '(')  {
                    has_bracket = true;
                    break;
                }

            if (has_bracket) {            
                while (left <= right && expr[left] != '(')  ++left;
                ++left;
                while (left <= right && expr[right] != ')') --right;
                --right;
                return solve(expr, left, right, env);                            
            };


            while (left <= right && isspace(expr[left])) ++left;
            while (left <= right && isspace(expr[right])) --right;

            ret[""] = 0;            
            if (left > right) return ret;                       

            auto temp = expr.substr(left, right-left+1);

            if (isdigit(expr[left])) 
                ret[""] = stoi(expr.substr(left, right-left+1));
            else {
                if (env.count(temp)) {
                    ret[""] = env[temp];
                }
                else {
                    ret[temp] = 1;
                };
            };

            return ret;            
        };             

        if (op == '*') {

            auto l = solve(expr, left, pos-1, env);
            auto r = solve(expr, pos+1, right, env);


            for (auto p: l)  {
                auto p1 = split(p.first);
                for (auto q: r) {
                    auto q1 = split(q.first);
                    auto coef = convert(add(p1, q1));                   
                    ret[coef] += p.second*q.second;                    
                };                                
            };

            return ret;

        }
        else {
            auto l = solve(expr, left ,pos-1, env);
            auto r = solve(expr, pos+1, right, env);

            for (auto p: l) {
                ret[p.first] += p.second;
            };
            int flag = 1;
            if (op == '-') flag = -1;
            for (auto q: r) {
                ret[q.first] += q.second*flag;
            };
            return ret;
        };        

    }

    int degree(string s) {
        auto r = split(s);
        int ret = 0;
        for (auto p: r) ret += p.second;
        return ret;
    };

    map<string, int> split(string s) {
        int i = 0;
        int n = s.size();

        map<string, int> ret;        
        string temp;

        while (i < n) {
            while (i < n && s[i] != '*') {
                temp.push_back(s[i++]);
            };
            ++i;
            ret[temp]++;      
            temp.clear();      
        };

        return ret;                
    };

    map<string, int> add(map<string, int> a, map<string, int> b) {
        map<string, int> ret;
        for (auto p: a)
            ret[p.first] += p.second;
        for (auto q: b)
            ret[q.first] += q.second;
        return ret;
    };

    string convert(map<string, int> a) {
        string temp;
        for (auto p: a) {
            if (p.second == 0) continue;
            for (int j = 0; j <p.second; ++j) {
                if (temp.size() != 0) temp.push_back('*');
                temp.append(p.first);
            }
        };
        return temp;

    }


};

class term(object):
    def __init__(self, coef):
        self.coef = coef
        self.var = []
    def __neg__(self):
        rt = term(-self.coef)
        rt.var = self.var[:]
        return rt

    def compareTuple(self):
        self.var.sort()
        return (-len(self.var), "*".join(self.var))
    def compareStr(self):
        self.var.sort()
        return "*".join(self.var)
    def __str__(self):
        if not self.var:
            return str(self.coef)
        return "".join([str(self.coef), "*", self.compareStr()])

class manyTerms(object):
    def __init__(self, term):
        self.terms = [term]
    # def pt(self):
    #     for t in self.terms:
    #         print t
    def result(self):
        self.terms = [t for t in self.terms if t.coef != 0]
        self.terms.sort(key = term.compareTuple)
        return map(str, self.terms)

    def add(self, other):
        for j in range(len(other.terms)):
            for i in range(len(self.terms)):
                if self.terms[i].compareStr() == other.terms[j].compareStr():
                    self.terms[i].coef += other.terms[j].coef
                    other.terms[j].coef = 0
                    break
        for j in range(len(other.terms)):
            if other.terms[j].coef != 0:
                self.terms.append(other.terms[j])

    def minus(self, other):
        for j in range(len(other.terms)):
            for i in range(len(self.terms)):
                if self.terms[i].compareStr() == other.terms[j].compareStr():
                    self.terms[i].coef -= other.terms[j].coef
                    other.terms[j].coef = 0
                    break

        for j in range(len(other.terms)):
            if other.terms[j].coef != 0:
                self.terms.append(-other.terms[j])
    def mul(self, other):
        ans = manyTerms(term(0))
        for j in range(len(other.terms)):
            for i in range(len(self.terms)):
                newTerm = term(self.terms[i].coef * other.terms[j].coef)
                newTerm.var = self.terms[i].var + other.terms[j].var
                ans.add(manyTerms(newTerm))
        return ans



class Solution(object):
    def basicCalculatorIV(self, expression, evalvars, evalints):
        """
        :type expression: str
        :type evalvars: List[str]
        :type evalints: List[int]
        :rtype: List[str]
        """
        self.d = {}
        for i in range(len(evalvars)):
            self.d[evalvars[i]] = evalints[i]
        return self.calcExp(expression).result()
    def calcExp(self, s):
        # first deal with ( and ) with stack
        s = s.strip()
        # iPlus = s.rfind("+")
        # iMinus = s.rfind("-")
        # iMul = s.rfind("*")
        iPlus = iMinus = iMul = -1
        level = 0
        for i in reversed(range(len(s))):
            if level == 0:
                if s[i] == "+" and iPlus == -1:
                    iPlus = i
                if s[i] == "-" and iMinus == -1:
                    iMinus = i
                if s[i] == "*" and iMul == -1:
                    iMul = i
            if s[i] == ")":
                level += 1
            if s[i] == "(":
                level -= 1

        if iPlus == -1 and iMinus == -1 and iMul == -1:
            if s[0] == "(":
                return self.calcExp(s[1:-1])
            if s in self.d:
                return manyTerms(term(self.d[s]))
            if ord("a")<=ord(s[0])<=ord("z"):
                tempTerm = term(1)
                tempTerm.var.append(s)
                return manyTerms(tempTerm)
            else:
                return manyTerms(term(int(s)))
        if iPlus == -1 and iMinus == -1:
            mx = iMul
        else:
            mx = max(iPlus, iMinus)
        left = self.calcExp(s[:mx])
        right = self.calcExp(s[mx+1:])
        if mx == iPlus:
            left.add(right)
            return left
        if mx == iMinus:
            left.minus(right)
            return left
        if mx == iMul:
            return left.mul(right)

