class Solution(object):
    def maximumXOR(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        res = 0

        for i in range(32):
            val = (1<<i)
            cnt0, cnt1 = 0, 0
            for x in nums:
                if x<val:
                    continue

                if x&val:
                    cnt1+=1
                else:
                    cnt0+=1

            if cnt1>0:
                res+=val

        return res

class Solution {
public:
    struct node{
        int x,y,k;
        node() {}
        node(int _x,int _y,int _k) { x=_x; y=_y; k=_k; }
    };
    int shortestPath(vector<vector<int>>& grid, int k) {
        queue<node> q;
        int n=grid.size(),m=grid[0].size();
        int d[n+10][m+10][k+10];
        memset(d, -1, sizeof(d));
        d[0][0][0] = 0; q.push(node(0,0,0));
        const int dx[4]={0,0,1,-1};
        const int dy[4]={1,-1,0,0};
        while (!q.empty()) {
            node u=q.front(); q.pop();
            for (int i=0;i!=4;++i) {
                int px=u.x+dx[i], py=u.y+dy[i];
                if (px>=0 && px<n && py>=0 && py<m && u.k+grid[px][py]<=k) {
                    if (d[px][py][u.k+grid[px][py]]==-1) {
                        d[px][py][u.k+grid[px][py]] = d[u.x][u.y][u.k] + 1;
                        q.push(node(px,py,u.k+grid[px][py]));
                    }
                }
            }
        }
        int ans=n*m+1;
        for (int i=0;i<=k;++i) if (d[n-1][m-1][i]!=-1)
            ans = min(ans, d[n-1][m-1][i]);
        if (ans==n*m+1) ans = -1;
        return ans;
    }
};

class Solution {
    public int shortestPath(int[][] a, int k) {
        int n = a.length, m = a[0].length;
        k = Math.min(k, n+m);
        Queue<int[]> q = new ArrayDeque<>();
        int[][][] d = new int[k+1][n][m];
        for(int i = 0;i <= k;i++){
        	for(int j = 0;j < n;j++){
        		Arrays.fill(d[i][j], 9999999);
        	}
        }
        d[0][0][0] = 0;
        q.add(new int[]{0, 0, 0});
        int[] dr = { 1, 0, -1, 0 };
		int[] dc = { 0, 1, 0, -1 };
        while(!q.isEmpty()){
        	int[] cur = q.poll();
        	int u = cur[0], r = cur[1], c = cur[2];
        	for(int t = 0;t < 4;t++){
        		int nr = r + dr[t], nc = c + dc[t];
        		if(nr >= 0 && nr < n && nc >= 0 && nc < m){
        			int nu = a[nr][nc] + u;
        			if(nu <= k && d[nu][nr][nc] > d[u][r][c] + 1){
        				d[nu][nr][nc] = d[u][r][c] + 1;
        				q.add(new int[]{nu, nr, nc});
        			}
        		}
        	}
        }

        int min = 9999999;
        for(int i = 0;i <= k;i++){
        	min =Math.min(min, d[i][n-1][m-1]);
        }
        if(min > 99999)return -1;
        return min;
    }
}

class Solution:
    def shortestPath(self, grid: List[List[int]], k: int) -> int:
        bfs = collections.deque()
        bfs.append((0, 0, 0, 0))
        seen = set()
        seen.add((0,0,0))
        n = len(grid)
        m = len(grid[0])

        while bfs:
            x, y, has, step = bfs.popleft()
            if x == n - 1 and y == m - 1:
                return step
            for i, j in ((x + 1, y), (x - 1, y), (x, y + 1), (x, y - 1)):
                if 0 <= i < n and 0 <= j < m:
                    if grid[i][j] == 1:
                        if (i, j, has + 1) not in seen and has < k:
                            bfs.append((i, j, has + 1, step + 1))
                            seen.add((i, j, has + 1))
                    else:
                        if (i, j, has) not in seen:
                            bfs.append((i, j, has, step + 1))
                            seen.add((i, j, has))

        return -1

/**
 * @param {number[][]} grid
 * @param {number} k
 * @return {number}
 */
var shortestPath = function(grid, k) {
  const m = grid.length - 1;
  const n = grid[0].length - 1;
  class Cell {
    constructor(x, y, count, el) {
      this.x = x;
      this.y = y;
      this.count = count;
      this.el = grid[y] && grid[y][x] === 1 ? el + 1 : el;
    }
  }

  const queue = [new Cell(0, 0, 0, 0)];
  const memo = new Set('0:0:0');
  let min = Infinity;
  while (queue.length) {
    const cell = queue.shift();
    let { x, y, count, el } = cell;
    const str = `${x}:${y}:${el}`;
    if (memo.has(str)) {
      continue;
    }
    memo.add(str);
    if (x < 0 || x > n || y < 0 || y > m) {
      continue;
    }
    if (el > k) {
      continue;
    }
    if (count >= min) {
      continue;
    }
    if (x === n && y === m) {
      min = Math.min(min, count);
      continue;
    }
    count++;
    queue.push(new Cell(x - 1, y, count, el));
    queue.push(new Cell(x + 1, y, count, el));
    queue.push(new Cell(x, y + 1, count, el));
    queue.push(new Cell(x, y - 1, count, el));
  }
  return min === Infinity ? -1 : min;
};

