class Node:
    def __init__(self):
        self.val = 0
        self.ch = {}

class Solution(object):
    def sumPrefixScores(self, words):
        """
        :type words: List[str]
        :rtype: List[int]
        """
        root = Node()

        def _add(word):
            u = root
            for ch in word:
                if ch not in u.ch:
                    u.ch[ch] = Node()
                u = u.ch[ch]
                u.val += 1

        def _query(word):
            r = 0
            u = root
            for ch in word:
                if ch not in u.ch:
                    break
                u = u.ch[ch]
                r += u.val
            return r

        for word in words: _add(word)
        return [_query(word) for word in words]

class Solution {
