class Trie {
    TrieNode root;

    public Trie() {
        root = new TrieNode();
    }

    public void insert(String word) {
        TrieNode node = root;
        int length = word.length();
        for (int i = 0; i < length; i++) {
            char letter = word.charAt(i);
            int index = letter - 'a';
            if (node.next[index] == null)
                node.next[index] = new TrieNode();
            node = node.next[index];
            node.count++;
        }
    }

    public int getScore(String word) {
        int score = 0;
        TrieNode node = root;
        int length = word.length();
        for (int i = 0; i < length; i++) {
            char letter = word.charAt(i);
            int index = letter - 'a';
            if (node.next[index] == null)
                return 0;
            else
                node = node.next[index];
            score += node.count;
        }
        return score;
    }
}

class TrieNode {
    int count;
    TrieNode[] next;

    public TrieNode() {
        count = 0;
        next = new TrieNode[26];
    }
}

