class Solution:
    def closestToTarget(self, arr: List[int], target: int) -> int:
        if target in arr:
            return 0
        old_arr = arr
        arr = [old_arr[0]]
        for i in range(1,len(old_arr)):
            if old_arr[i] != arr[-1]:
                arr.append(old_arr[i])
        best = abs(target - arr[0]) 
        for i in range(len(arr)):
            cur = arr[i]
            best = min(best, abs(target - cur))
            for j in range(i+1,len(arr)):
                cur &= arr[j]
                best = min(best, abs(target - cur))
                if cur < target:
                    break
        return best

fn calc(ta: &Vec<Vec<i32>>, l: usize, r: usize) -> i32 {
    let mut step = 1;
    let mut step_i = 0;
    let mut len = r - l + 1;
    while step < len {
        step *= 2;
        step_i += 1;
    }
    let mut ret = ta[l][0];

    let mut l = l;
    while l <= r {
        while step > r - l + 1 {
            step /= 2;
            step_i -= 1;
        }
        ret &= ta[l][step_i];
        l += step;
    }
    ret
}

impl Solution {
    pub fn closest_to_target(arr: Vec<i32>, target: i32) -> i32 {
        let n = arr.len();
        let mut ta = vec![vec![]; n];
        for i in 0..n {
            ta[i].push(arr[i]);
        }
        let mut step = 1;
        let mut step_i = 0;
        while step * 2 <= n {
            for i in 0..=(n - step * 2) {
                let x = ta[i][step_i] & ta[i + step][step_i];
                ta[i].push(x);
            }
            step *= 2;
            step_i += 1;
        }

        let mut ans = 99999999;
        for i in 0..n {
            //println!("Loop {} {}", i, ans);
            let mut l = i;
            let mut r = n - 1;
            let mut and_l = calc(&ta, i, l);
            let mut and_r = calc(&ta, i, r);
            //println!("{} {} {} {}", l, r, and_l, and_r);
            if and_l <= target {
                ans = ans.min(target - and_l);
                continue;
            }
            if and_r >= target {
                ans = ans.min(and_r - target);
                continue;
            }
            while l + 1 < r {
                let mut m = (l + r) / 2;
                let and_m = calc(&ta, i, m);
                //println!("{} {}", m, and_m);
                if and_m <= target {
                    and_r = and_m;
                    r = m;
                } else {
                    and_l = and_m;
                    l = m;
                }
            }
            ans = ans.min(and_l - target);
            ans = ans.min(target - and_r);
            //println!("ans {}", ans);
        }
        ans
    }
}

/**
 * @param {number[]} arr
 * @param {number} target
 * @return {number}
 */
var closestToTarget = function(arr, target) {
    let min = Math.abs(arr[0] - target)
    for (let i = 0; i < arr.length && min > 0; i++) {
        let result = arr[i]
        if (Math.abs(result - target) < min) {
            min = Math.abs(result - target)
        }
        for (let j = 1; j + i < arr.length && result > target; j++) {
            result = result & arr[i + j]
            let diff = Math.abs(result - target)
            if (diff < min) {
                min = diff
            }
        }
        if (result > target) {
            break
        }
    }
    return min
};

const int N_MAX = 105;
const long long LL_INF = (long long) 2e18 + 5;

class Solution {
public:
    int n;
    vector<int> leaves;
    long long dp[N_MAX][N_MAX];

    long long solve(int start, int end) {
        if (end - start <= 1)
            return 0;

        long long &answer = dp[start][end];

        if (answer >= 0)
            return answer;

        answer = LL_INF;

        for (int i = start + 1; i < end; i++) {
            int left = 0, right = 0;

            for (int j = start; j < i; j++)
                left = max(left, leaves[j]);

            for (int j = i; j < end; j++)
                right = max(right, leaves[j]);

            answer = min(answer, left * right + solve(start, i) + solve(i, end));
        }

        return answer;
    }

    int mctFromLeafValues(vector<int>& arr) {
        leaves = arr;
        n = leaves.size();
        memset(dp, -1, sizeof(dp));
        return solve(0, n);
    }
};

class Solution {
    public int mctFromLeafValues(int[] arr) {
        int n =arr.length;
        long[][] dp = new long[n][n];
        for(int len = 1;len <= n;len++){
        	for(int i = 0;i+len-1 < n;i++){
        		int j = i+len-1;
        		if(len == 1){
        			dp[i][i] = 0;
        		}else{
        			dp[i][j] = Long.MAX_VALUE;
        			for(int k = i+1;k <= j;k++){
        				int maxl = 0;
        				for(int l = i;l < k;l++){
        					maxl = Math.max(maxl, arr[l]);
        				}
        				int maxr = 0;
        				for(int l = k;l <= j;l++){
        					maxr = Math.max(maxr, arr[l]);
        				}
        				dp[i][j] = Math.min(dp[i][j], dp[i][k-1] + dp[k][j] + maxl*maxr);
        			}
        		}
        	}
        }
        return (int)dp[0][n-1];
    }
}

class Solution:
    def mctFromLeafValues(self, arr: List[int]) -> int:
        n = len(arr)
        mx = [[-float('inf') for i in range(n+1)] for j in range(n+1)]
        DP = [[0 for i in range(n+1)] for j in range(n+1)]
        for i in range(n):
            for j in range(i, n):
                mx[i][j+1] = max(mx[i][j], arr[j])
        for ln in range(2, n+1):
            for i in range(n-ln+1):
                j = i + ln
                DP[i][j] = float('inf')
                for k in range(i+1, j):
