                    #print(i, j, k, DP[i][j])
                #print("final:", ln, i, j, DP[i][j])
        return DP[0][n]

class Solution(object):
    def mctFromLeafValues(self, arr):
        """
        :type arr: List[int]
        :rtype: int
        """
        INF = 1e100
        n = len(arr)
        f = [[INF] * n for _ in xrange(n)]
        d = [[0] * n for _ in xrange(n)]
        for i in xrange(n):
            f[i][i] = 0
            d[i][i] = arr[i]
        for i in xrange(n):
            for j in xrange(i + 1, n):
                d[i][j] = max(d[i][j - 1], arr[j])
        for i in xrange(n):
            for j in xrange(i - 1, -1, -1):
                for k in xrange(j, i):
                    f[j][i] = min(f[j][i], f[j][k] + f[k + 1][i] + d[j][k] * d[k + 1][i])
        return f[0][n - 1]

