    console.log(dp)
    let [max,val] = tools(0,arr.length-1);
    return val;

    function tools(i,j){
        if(i===j)return [arr[i],0];//
        if(dp[i][j]!="#")return dp[i][j];
        let max;
        let val =Infinity;

        for(let k=i;k<j;k++){
            let [maxNodeLeft,valLeft] = tools(i,k);
            let [maxNodeRight,valRight] = tools(k+1,j);

            let nowVal = maxNodeLeft * maxNodeRight + valLeft + valRight;
            if(nowVal<val){
                val=nowVal;
                max = Math.max(maxNodeLeft,maxNodeRight);
            }
        }
        return dp[i][j]=[max,val];
    }
};

